UPDATE COM_FATURA_FORNECEDOR SET FAT_VALOR_SALDO = 0
WHERE FAT_VALOR_PAGO = FAT_VALOR_TOTAL;

SET v_CON_ = CONCAT("

SELECT 
	C.FAT_CODIGO, 
	C.FAT_REFERENCIA, 
	C.FAT_DATA_EMISSAO, 
	DOC_DESCRICAO, 
	C.FAT_NOME_FORNECEDOR, 
