	C.FAT_VALOR_ADUANEIRA, 
	C.FAT_VALOR_HONORARIO_DESPACHANTE, 
	C.FAT_VALOR_TRANSPORTACAO_LOCAL, 
	C.FAT_OUTROS_ENCARGOS,
	C.FAT_TOTAL_ENCARGOS
FROM 
COM_FATURA_FORNECEDOR C 
INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = C.FAT_CODIGO_DOCUMENTO
INNER JOIN COM_SERIE_DOCUMENTACAO ON SER_CODIGO = C.FAT_CODIGO_SERIE
LEFT JOIN GER_ENTIDADE ON ENT_CODIGO = C.FAT_CODIGO_FORNECEDOR
