WHERE C.FAT_STATUS = 1 AND C.FAT_CODIGO_FILIAL=",FILIAL, " AND C.FAT_REFERENCIA LIKE '%",REFERENCIA,"%' AND 
C.FAT_NOME_FORNECEDOR LIKE '%",FORNECEDOR,"%' AND C.FAT_REFERENCIA_FORNECEDOR LIKE '%",REFERENCIA_FORNECEDOR,"%' AND  C.FAT_STATUS_DOCUMENTO =8 AND C.FAT_NUMERACAO > 0 "); 
 
IF(ANO > 0) THEN
 SET v_CON_ = CONCAT(v_CON_," AND SER_ANO =",ANO); 
