IF( VALIDADE_INI IS NOT NULL AND  VALIDADE_TERM IS NOT NULL) THEN
  SET v_CON_ = CONCAT(v_CON_," AND C.FAT_DATA_VALIDADE BETWEEN '", VALIDADE_INI, "' AND '",  VALIDADE_TERM, "'");
ELSE IF( VALIDADE_INI IS NOT NULL AND  VALIDADE_TERM IS NULL) THEN
  SET v_CON_ = CONCAT(v_CON_," AND C.FAT_DATA_VALIDADE >= '", VALIDADE_INI, "'");
ELSE IF( VALIDADE_INI IS NULL AND  VALIDADE_TERM IS NOT NULL) THEN
  SET v_CON_ = CONCAT(v_CON_," AND C.FAT_DATA_VALIDADE <= '",  VALIDADE_TERM, "'");
END IF;
END IF;
END IF;
IF(DOCUMENTO > 0) THEN
SET v_CON_=CONCAT(v_CON_," AND C.FAT_CODIGO_DOCUMENTO = ", DOCUMENTO);
END IF;

IF(SITUACAO > 0) THEN
 SET v_CON_=CONCAT(v_CON_," AND C.FAT_STATUS = 1 ");
