IF(DOCUMENT_STATUS > 0) THEN
 SET v_CON_=CONCAT(v_CON_," AND C.FAT_STATUS_DOCUMENTO = ", DOCUMENT_STATUS);
  IF(DOCUMENT_STATUS = 8) THEN
    SET v_CON_=CONCAT(v_CON_," C.AND FAT_NUMERACAO >0");
