-- Procedure structure for stp_COM_FATURA_FORNECEDOR_OBTERPORPK
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_COM_FATURA_FORNECEDOR_OBTERPORPK`;
delimiter ;;
CREATE PROCEDURE `stp_COM_FATURA_FORNECEDOR_OBTERPORPK`(IN `CODIGO` INT)
BEGIN
	 

SELECT FAT_CODIGO, FAT_CODIGO_FILIAL, FAT_CODIGO_ARMAZEM, FAT_CODIGO_DOCUMENTO, FAT_CODIGO_SERIE, FAT_CODIGO_FORNECEDOR, FAT_DATA_EMISSAO,
FAT_DATA_VALIDADE, FAT_CODIGO_MOEDA, FAT_CAMBIO , FAT_CONDICAO_PAGAMENTO, IFNULL(FAT_CODIGO_EXPEDICAO, -1) AS FAT_CODIGO_EXPEDICAO, SUM(FAT_DESCONTO) AS FAT_DESCONTO,
FAT_NUMERACAO, 
FAT_REFERENCIA, FAT_STATUS_DOCUMENTO, SUM(FAT_VALOR_BRUTO) AS FAT_VALOR_BRUTO, SUM(FAT_TOTAL_DESCONTOS) AS FAT_TOTAL_DESCONTOS, 
SUM(FAT_TOTAL_IMPOSTOS) AS FAT_TOTAL_IMPOSTOS, SUM(FAT_VALOR_TOTAL) AS FAT_VALOR_TOTAL, FAT_STATUS_PAGAMENTO, 
FAT_OBSERVACOES, FAT_DOCUMENTO_ORIGEM, FAT_NUMERO_DOC_ORIGEM, SUM(FAT_VALOR_PAGO) AS FAT_VALOR_PAGO, SUM(FAT_VALOR_SALDO) AS FAT_VALOR_SALDO, 
SUM(FAT_TROCO) AS FAT_TROCO, FAT_NOME_FORNECEDOR, SUM(FAT_DESCONTO_FORNECEDOR) AS FAT_DESCONTO_FORNECEDOR, 
FAT_DATA_CARGA, FAT_MORADA_CARGA, FAT_LOCAL_CARGA, FAT_DATA_DESCARGA, FAT_MORADA_DESCARGA, FAT_LOCAL_DESCARGA, 
FAT_NOTAS_INTERNAS, FAT_MOTIVO_ANULACAO, FAT_STATUS, DOC_DESCRICAO, FAT_STATUS_DOCUMENTO, FAT_PARCELA, FAT_NOTAS_COMERCIAIS,
FAT_RESPONSAVEL_CARGA, FAT_RESPONSAVEL_ENTREGA, FAT_RECEPCAO, FAT_CONTACTO_DESTINO, FAT_CONTACTO_DESTINO_ALT, FAT_ENTITY_BILL_ID,
FAT_MATRICULA_CARGA, FAT_BARCODE, FAT_CODIGO_COMPRADOR, V.ENT_NOME_COMPLETO AS VENDEDOR, UTI_NOME AS FAT_CREATED_BY, FAT_CREATED_DATE,
FAT_REFERENCIA_FORNECEDOR, FAT_VALOR_FRETE, FAT_VALOR_SEGURO, FAT_IVA_IMPORTACAO, FAT_VALOR_ADUANEIRA, FAT_VALOR_HONORARIO_DESPACHANTE, FAT_VALOR_TRANSPORTACAO_LOCAL, FAT_OUTROS_ENCARGOS, FAT_TOTAL_ENCARGOS, C.ENT_IDENTIFICACAO

FROM COM_FATURA_FORNECEDOR
INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = FAT_CODIGO_DOCUMENTO
LEFT JOIN SIS_UTILIZADOR ON UTI_UTILIZADOR = FAT_CREATED_BY
LEFT JOIN GER_ENTIDADE V ON V.ENT_CODIGO = FAT_CODIGO_COMPRADOR
INNER JOIN GER_ENTIDADE C ON C.ENT_CODIGO = FAT_CODIGO_FORNECEDOR
WHERE FAT_CODIGO = CODIGO;
END
;;
