-- ----------------------------
-- Procedure structure for stp_COM_FATURA_FORNECEDOR_OBTERVENDASDIARIAS
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_COM_FATURA_FORNECEDOR_OBTERVENDASDIARIAS`;
delimiter ;;
CREATE PROCEDURE `stp_COM_FATURA_FORNECEDOR_OBTERVENDASDIARIAS`(IN `FILIAL` int,IN `INICIO` date,IN `TERMINO` date, WAREHOUSE_ID INT, UTILIZADOR VARCHAR(20))
BEGIN
	#Routine body goes here...
DECLARE PERFIL_ID INT DEFAULT 0;
DECLARE UTILIZADOR_ID INT DEFAULT 0;

DECLARE v_CON_ LONGTEXT;
SELECT UTI_CODIGO_PERFIL, UTI_CODIGO INTO PERFIL_ID, UTILIZADOR_ID FROM SIS_UTILIZADOR WHERE UTI_UTILIZADOR = UTILIZADOR;


IF(INICIO IS NULL) THEN 
SELECT MIN(FAT_DATA_EMISSAO) INTO INICIO FROM COM_FATURA_FORNECEDOR; 
END IF;

IF(TERMINO IS NULL) THEN 
SELECT MAX(FAT_DATA_EMISSAO) INTO TERMINO FROM COM_FATURA_FORNECEDOR; 
END IF;
SET v_CON_ = CONCAT("
SELECT FAT_DATA_EMISSAO, SUM(FAT_VALOR_TOTAL), SUM(FAT_TOTAL_IMPOSTOS), SUM(FAT_VALOR_BRUTO), SUM(FAT_TOTAL_DESCONTOS),
(
SELECT IFNULL(SUM(
(CASE WHEN MOV_TRANSACAO='E' THEN
		  MOV_VALOR 
		  WHEN (MOV_TRANSACAO='S' AND MOV_VALOR > 0 -MOV_TRANSACAO) THEN (MOV_VALOR *(-1))
     ELSE 0 
    END)
),0) AS MOV_VALOR FROM FIN_MOVIMENTO_CAIXA  
WHERE MOV_DATA = FAT_DATA_EMISSAO
) AS RECEBIDO 

FROM COM_FATURA_FORNECEDOR
INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = FAT_CODIGO_DOCUMENTO
WHERE DOC_FORMATO IN ('INVOICE', 'INVOICE_R') AND FAT_STATUS=1 AND
FAT_STATUS_DOCUMENTO = 8 AND FAT_CODIGO_FILIAL=", FILIAL, " AND FAT_DATA_EMISSAO BETWEEN '", INICIO, "' AND '", TERMINO, "'");

IF(WAREHOUSE_ID > 0) THEN
 SET v_CON_=CONCAT(v_CON_, " AND D.FAT_CODIGO_ARMAZEM = ", WAREHOUSE_ID);
ELSE
 SET v_CON_=CONCAT(v_CON_," AND FAT_CODIGO_ARMAZEM IN (SELECT WAR_PERM_ARMAZEM_ID FROM GER_ARMAZEM_PERMISSOES
WHERE WAR_PERM_STATUS = 1 AND (WAR_PERM_PERFIL_ID =",PERFIL_ID," OR WAR_PERM_UTILIZADOR_ID = ",UTILIZADOR_ID,"))");
END IF;
SET v_CON_=CONCAT(v_CON_, " GROUP BY FAT_DATA_EMISSAO ");

SET @stmt_str = v_CON_;
PREPARE stmt FROM @stmt_str;
EXECUTE stmt;
DEALLOCATE PREPARE stmt;

END
;;
delimiter ;

-- ----------------------------
-- Procedure structure for stp_COM_FATURA_IMPRESSAO
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_COM_FATURA_IMPRESSAO`;
delimiter ;;
CREATE PROCEDURE `stp_COM_FATURA_IMPRESSAO`(IN `CODIGO` int)
BEGIN
	#Routine body goes here...
DECLARE REFERENCIA VARCHAR(20) DEFAULT "";
DECLARE SENT_TO_CUSTOMER INT DEFAULT 0;

SELECT FAT_REFERENCIA, FAT_CUSTOMER_SENT INTO REFERENCIA, SENT_TO_CUSTOMER FROM COM_FATURA_CLIENTE WHERE FAT_CODIGO = CODIGO; 

IF(SENT_TO_CUSTOMER IS NULL OR SENT_TO_CUSTOMER=0) THEN 
	UPDATE COM_FATURA_CLIENTE SET 
	FAT_CUSTOMER_SENT = 1,
	FAT_CUSTOMER_SENT_DATE = NOW()
	WHERE FAT_CODIGO = CODIGO;
	SET SENT_TO_CUSTOMER =0;
END IF;
 
IF(NOT EXISTS(SELECT * FROM COM_FATURA_CLIENTE_ITEM WHERE FAT_ITEM_CODIGO_FATURA = CODIGO)) THEN

SELECT DOC_DESCRICAO, CONCAT(DOC_SIGLA," ",SER_DESCRICAO,"/", FAT_NUMERACAO) AS FAT_REFERENCIA, FAT_DATA_EMISSAO, PAG_DESCRICAO, FAT_VALOR_TOTAL, FAT_NOME_CLIENTE, C.ENT_TELEFONE, 
C.ENT_TELEFONE_ALTERNATIVO, C.ENT_EMAIL, C.ENT_RUA, C.ENT_BAIRRO, MOE_SIGLA, FAT_CAMBIO, 
IFNULL(FAT_DESCONTO_CLIENTE, 0) AS FAT_DESCONTO_CLIENTE, IFNULL(FAT_DESCONTO, 0) AS FAT_DESCONTO, FAT_DATA_VALIDADE, 
ART_REFERENCIA, ART_CODIGO_BARRAS, 
IFNULL(FAT_ITEM_DESGINATION, ART_DESIGNACAO) AS ART_DESIGNACAO, I.FAT_ITEM_QUANTIDADE, FAT_ITEM_PRECO, FAT_ITEM_DESCONTO_LINHA, FAT_ITEM_IMPOSTO, FAT_ITEM_TOTAL, FAT_VALOR_BRUTO, FAT_TOTAL_DESCONTOS, FAT_TOTAL_IMPOSTOS, 
CONCAT("CONTRIBUINTE: ",C.ENT_IDENTIFICACAO) AS CONTRIBUINTE, DOC_FORMATO, FAT_NOTAS_COMERCIAIS, FAT_ITEM_VALOR_IMPOSTOS, FAT_ITEM_COMENTARIOS, FAT_CREATED_DATE, 
FAT_DATA_CARGA, FAT_MATRICULA_CARGA, FAT_LOCAL_CARGA, FAT_LOCAL_DESCARGA, FAT_DATA_DESCARGA, FAT_RECEPCAO, 
