I.FAT_ITEM_CODIGO_FATURA = FAT_CODIGO
LEFT JOIN COM_SERIE_DOCUMENTACAO ON SER_CODIGO = FAT_CODIGO_SERIE
LEFT JOIN COM_DOCUMENTO ON DOC_CODIGO = SER_CODIGO_DOCUMENTO
LEFT JOIN COM_CONDICAO_PAGAMENTO ON PAG_CODIGO = FAT_CONDICAO_PAGAMENTO 
LEFT JOIN GER_ENTIDADE C ON C.ENT_CODIGO = FAT_CODIGO_CLIENTE
LEFT JOIN FIN_MOEDA  ON MOE_CODIGO = FAT_CODIGO_MOEDA
LEFT JOIN GER_ARTIGO ON ART_CODIGO = I.FAT_ITEM_CODIGO_ARTIGO 
