delimiter ;;
CREATE PROCEDURE `stp_COM_FATURA_REFERENCIA_SUSPENSAO_ADICIONAR`(IN CODIGO INT, IN `DESIGNACAO` varchar(120),IN VALOR DECIMAL,IN `UTILIZADOR` varchar(20),IN `DATA_SUSP` date,IN `FILIAL` int, OUT LSTID INT)
BEGIN
	#Routine body goes here...
IF(CODIGO<=0)THEN
	SELECT COM_FAT_REF_SUSP_CODIGO INTO CODIGO FROM COM_FATURA_REFERENCIA_SUSPENDER WHERE COM_FAT_REF_SUSP_DESIGNACAO= DESIGNACAO
	AND COM_FAT_REF_SUSP_DATA = DATA_SUSP;
		IF(CODIGO IS NULL OR CODIGO <=0) THEN

			INSERT INTO COM_FATURA_REFERENCIA_SUSPENDER
			(COM_FAT_REF_SUSP_DESIGNACAO,COM_FAT_REF_SUSP_VALOR_TOTAL,COM_FAT_REF_SUSP_UTILIZADOR,COM_FAT_REF_SUSP_DATA,	     COM_FAT_REF_SUSP_FILIAL)
			VALUES(DESIGNACAO,VALOR,UTILIZADOR,CURDATE(),FILIAL);
			SELECT LAST_INSERT_ID() INTO LSTID;
		ELSE

		 SET LSTID = CODIGO;
		END IF;
		
	ELSE
	DELETE FROM COM_FATURA_SUSPENSA WHERE COM_FAT_SUSP_REFERENCIA=CODIGO;
	UPDATE COM_FATURA_REFERENCIA_SUSPENDER SET 
	COM_FAT_REF_SUSP_DESIGNACAO= DESIGNACAO,
	COM_FAT_REF_SUSP_VALOR_TOTAL=VALOR,
	COM_FAT_REF_SUSP_DATA = CURDATE()
	WHERE COM_FAT_REF_SUSP_CODIGO=CODIGO;
	SET LSTID = CODIGO;
	END IF;
END
;;
