-- Procedure structure for stp_COM_GUIA_ENTREGA_ADICIONAR
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_COM_GUIA_ENTREGA_ADICIONAR`;
delimiter ;;
CREATE PROCEDURE `stp_COM_GUIA_ENTREGA_ADICIONAR`(IN `CODIGO` int,IN `DATA_GUIA` date,IN `DATA_ENTREGA` datetime,IN `DOCUMENTO` int,IN `SERIE` int,IN `ORIGEM` int,IN `FUNCIONARIO` int,IN `MOTORISTA` varchar(255),IN `UTILIZADOR` varchar(20),IN `CLIENTE` varchar(120),
OUT LSTID INT)
BEGIN
	#Routine body goes here...
DECLARE DOCUMENT_SIMBOL VARCHAR(10) DEFAULT "";
DECLARE DOCUMENT_NUMBER INT DEFAULT 0;
DECLARE ANO_FATURACAO INT DEFAULT 0;
DECLARE ACTIVO INT DEFAULT 1;
DECLARE PREFIXO VARCHAR(12);
DECLARE DOC_TYPE VARCHAR(10) DEFAULT "";
DECLARE PARCELA_PRINCIPAL VARCHAR(20) DEFAULT ""; 
DECLARE PRIOR_HASH LONGTEXT DEFAULT ""; 
DECLARE DOC_FORMATE VARCHAR(50) DEFAULT "";
DECLARE ORGINAL_DOC_VALUE DECIMAL(18,5) DEFAULT 0;
DECLARE REFERENCIA VARCHAR(50) DEFAULT NULL;

IF(CODIGO IS NULL OR CODIGO <=0) THEN
INSERT INTO COM_GUIA_ENTREGA (
GUIA_DATA,
GUIA_DATA_ENTREGA,
GUIA_CODIGO_DOCUMENTO,
GUIA_CODIGO_SERIE,
GUIA_DOCUMENTO_ORIGEM,
GUIA_CODIGO_FUNCIONARIO,
GUIA_CODIGO_MOTORISTA,
GUIA_CREATED_BY,
GUIA_CREATED_DATE,
GUIA_UPDATED_BY,
GUIA_UPDATED_DATE,
GUIA_RECEPTOR_CLIENTE
) VALUES(DATA_GUIA, DATA_ENTREGA, DOCUMENTO, SERIE, ORIGEM, FUNCIONARIO, MOTORISTA, UTILIZADOR, NOW(), UTILIZADOR, NOW(), CLIENTE);
SELECT LAST_INSERT_ID() INTO LSTID;
 
			 SELECT DOC_SIGLA, DOC_FORMATO INTO DOCUMENT_SIMBOL, DOC_FORMATE FROM COM_DOCUMENTO WHERE DOC_CODIGO = DOCUMENTO;
			 SELECT SER_NUMERACAO, SER_CODIGO INTO DOCUMENT_NUMBER, ANO_FATURACAO FROM COM_SERIE_DOCUMENTACAO WHERE SER_CODIGO = SERIE;
        SET DOCUMENT_NUMBER = DOCUMENT_NUMBER+1;
        IF(DOCUMENT_NUMBER < 10) THEN 
          SET PREFIXO = CONCAT("00000",DOCUMENT_NUMBER); 
        ELSE IF(DOCUMENT_NUMBER >= 10 AND DOCUMENT_NUMBER <100) THEN 
          SET PREFIXO = CONCAT("0000",DOCUMENT_NUMBER); 
        ELSE IF(DOCUMENT_NUMBER >= 100 AND DOCUMENT_NUMBER <1000) THEN 
          SET PREFIXO = CONCAT("000",DOCUMENT_NUMBER); 
        ELSE IF(DOCUMENT_NUMBER >= 1000 AND DOCUMENT_NUMBER <10000) THEN 
          SET PREFIXO = CONCAT("00",DOCUMENT_NUMBER); 
        ELSE IF(DOCUMENT_NUMBER >= 10000 AND DOCUMENT_NUMBER <100000) THEN 
          SET PREFIXO = CONCAT("0",DOCUMENT_NUMBER); 
        ELSE IF(DOCUMENT_NUMBER >= 100000) THEN 
          SET PREFIXO = DOCUMENT_NUMBER; 
        END IF;
        END IF;
        END IF;
        END IF;
        END IF;
				END IF;
				
			 SET REFERENCIA = CONCAT(DOCUMENT_SIMBOL," ",ANO_FATURACAO,"/",PREFIXO);
			 UPDATE COM_GUIA_ENTREGA SET
			 GUIA_NUMERACAO = DOCUMENT_NUMBER,
			 GUIA_REFERENCIA = REFERENCIA
			 WHERE GUIA_CODIGO = CODIGO;

			 UPDATE COM_SERIE_DOCUMENTACAO SET
			 SER_NUMERACAO = SER_NUMERACAO + 1
			 WHERE SER_CODIGO = SERIE;

 

END IF;
END
;;
delimiter ;

