CREATE PROCEDURE `stp_COM_GUIA_ENTREGA_EXTRACT_LIST`(IN `CUSTOMER_ID` int)
BEGIN
	#Routine body goes here...

SELECT IFNULL(GUIA_REFERENCIA, CONCAT(GUIA_CODIGO, "/", YEAR(GUIA_DATA_ENTREGA))), IFNULL(FAT_ITEM_DESGINATION, ART_DESIGNACAO) AS DESIGNACAO_PRODUTO, MAR_DESCRICAO, CAT_DESCRICAO, IFNULL(GUIA_ITEM_QUANTIDADE_ROLO, 0) AS GUIA_ITEM_QUANTIDADE_ROLO, IFNULL(GUIA_ITEM_QUANT_POR_ROLO, 0) as GUIA_ITEM_QUANT_POR_ROLO,
FAT_ITEM_QUANTIDADE, (GUIA_ITEM_QUANTIDADE_ENTREGUE - FAT_ITEM_QUANTIDADE) AS EXCESSO,  GUIA_ITEM_QUANTIDADE_ENTREGUE,
FAT_NOME_CLIENTE, GUIA_CODIGO_MOTORISTA, FAT_REFERENCIA, FAT_DATA_EMISSAO, "" AS FATURA, GUIA_DATA_ENTREGA, ENT_NOME_COMPLETO, FAT_CODIGO_CLIENTE
FROM COM_GUIA_ENTREGA_ITEM
INNER JOIN COM_GUIA_ENTREGA ON GUIA_CODIGO = GUIA_ITEM_GUIA_ID
INNER JOIN COM_FATURA_CLIENTE_ITEM ON FAT_ITEM_CODIGO = GUIA_ITEM_CODIGO_ARTIGO
INNER JOIN GER_ARTIGO ON ART_CODIGO = FAT_ITEM_CODIGO_ARTIGO
INNER JOIN GER_CATEGORIA ON CAT_CODIGO = ART_CATEGORIA
LEFT JOIN GER_MARCA ON MAR_CODIGO = ART_MARCA
INNER JOIN COM_FATURA_CLIENTE ON FAT_CODIGO = GUIA_DOCUMENTO_ORIGEM
INNER JOIN REST_FUNCIONARIOS ON GAR_CODIGO = GUIA_CODIGO_FUNCIONARIO
INNER JOIN GER_ENTIDADE F ON F.ENT_CODIGO = GAR_CODIGO_ENTIDADE
WHERE FAT_CODIGO_CLIENTE = CUSTOMER_ID AND (GUIA_STATUS IS NULL OR GUIA_STATUS = 1);

END
;;
