IN GUIA_ID INT)
BEGIN
	#Routine body goes here... 

DECLARE READY_DATE DATETIME DEFAULT NULL;

SELECT FAT_ITEM_DATA_TERMINO INTO READY_DATE FROM COM_FATURA_CLIENTE_ITEM WHERE FAT_ITEM_CODIGO = ITEM_CODIGO;

IF(READY_DATE IS NULL) THEN

	UPDATE COM_FATURA_CLIENTE_ITEM SET 
	FAT_ITEM_DATA_TERMINO = NOW(),
	FAT_ITEM_END_BY = UTILIZADOR,
	FAT_ITEM_END_DATE = NOW() 
	WHERE FAT_ITEM_CODIGO = ITEM_CODIGO;
END IF;

UPDATE COM_FATURA_CLIENTE_ITEM SET  
FAT_ITEM_END_BY=DELIVERMAN,
FAT_ITEM_DATA_ENTREGA=DATA_ENTREGA, 
FAT_ITEM_UPDATED_BY=UTILIZADOR
WHERE FAT_ITEM_CODIGO = ITEM_CODIGO;





INSERT INTO COM_GUIA_ENTREGA_ITEM(
GUIA_ITEM_GUIA_ID,
GUIA_ITEM_CODIGO_ARTIGO,
GUIA_ITEM_QUANTIDADE_ENTREGUE, GUIA_ITEM_QUANTIDADE_ROLO,
GUIA_ITEM_QUANT_POR_ROLO
)
VALUES(GUIA_ID, ITEM_CODIGO, QUANTIDADE, 0, 0);

IF(NOT EXISTS(SELECT * FROM COM_FATURA_CLIENTE_ITEM WHERE FAT_ITEM_CODIGO_FATURA = DOCUMENT_ID AND FAT_ITEM_DATA_ENTREGA IS NULL)) THEN
	UPDATE COM_FATURA_CLIENTE SET 
	FAT_STATUS_DOCUMENTO = 8,
	FAT_DATA_ENTREGA = NOW()
	WHERE FAT_CODIGO = DOCUMENT_ID;  
END IF;
 
 

END
;;
