-- Procedure structure for stp_COM_GUIA_ENTREGA_OBTERPORFILTRO
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_COM_GUIA_ENTREGA_OBTERPORFILTRO`;
delimiter ;;
CREATE PROCEDURE `stp_COM_GUIA_ENTREGA_OBTERPORFILTRO`(IN `CODIGO` int,IN `REFERENCIA` varchar(20),IN `DATA_INI` date,IN `DATA_TERM` date,IN `FUNCIONARIO_ID` int,IN `SERIE` int,IN `CUSTOMER_ID` int)
BEGIN
	#Routine body goes here...
DECLARE v_CON_ LONGTEXT;

SET v_CON_ = CONCAT(" SELECT G.*, FAT_NOME_CLIENTE, FAT_REFERENCIA, ENT_NOME_COMPLETO FROM COM_GUIA_ENTREGA G
INNER JOIN COM_FATURA_CLIENTE ON FAT_CODIGO = GUIA_DOCUMENTO_ORIGEM
INNER JOIN REST_FUNCIONARIOS ON GAR_CODIGO = GUIA_CODIGO_FUNCIONARIO
INNER JOIN GER_ENTIDADE ON ENT_CODIGO = GAR_CODIGO_ENTIDADE 
");

IF(CODIGO > 0) THEN
 SET v_CON_ = CONCAT(v_CON_," WHERE GUIA_CODIGO = ", CODIGO);
ELSE 
  SET v_CON_ = CONCAT(v_CON_," WHERE (GUIA_STATUS IS NULL OR GUIA_STATUS = 1) ");
	IF(REFERENCIA IS NOT NULL AND REFERENCIA !="") THEN
	 SET v_CON_ = CONCAT(v_CON_,"  AND GUIA_REFERENCIA LIKE '%",REFERENCIA,"%' ");
	END IF; 

	IF(FUNCIONARIO_ID > 0) THEN
	 SET v_CON_ = CONCAT(v_CON_," AND GUIA_CODIGO_FUNCIONARIO = ", FUNCIONARIO_ID);
	END IF;

	IF(SERIE > 0) THEN
	 SET v_CON_ = CONCAT(v_CON_," AND GUIA_CODIGO_SERIE = ", SERIE);
	END IF;

	IF(CUSTOMER_ID > 0) THEN
	 SET v_CON_ = CONCAT(v_CON_," AND FAT_CODIGO_CLIENTE = ", CUSTOMER_ID);
	END IF;


	IF((DATA_INI !='' AND DATA_INI IS NOT NULL) AND (DATA_TERM !='' AND DATA_TERM IS NOT NULL)) THEN
		SET v_CON_ = CONCAT(v_CON_," AND GUIA_DATA_ENTREGA BETWEEN '",DATA_INI, "' AND '", DATA_TERM, "'");
	ELSE IF(DATA_INI IS NOT NULL AND DATA_TERM IS NULL) THEN
		SET v_CON_ = CONCAT(v_CON_," AND GUIA_DATA_ENTREGA >= '",DATA_INI, "'");
	ELSE IF(DATA_INI IS NULL AND DATA_TERM IS NOT NULL) THEN
		SET v_CON_ = CONCAT(v_CON_," AND GUIA_DATA_ENTREGA <= '", DATA_TERM, "'");
	END IF;
	END IF;
	END IF; 
	END IF; 
#SELECT v_CON_;
SET @stmt_str = v_CON_;
PREPARE stmt FROM @stmt_str;
EXECUTE stmt;
DEALLOCATE PREPARE stmt;
END
;;
delimiter ;

-- ----------------------------
-- Procedure structure for stp_COM_GUIA_ENTREGA_TOPRINT
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_COM_GUIA_ENTREGA_TOPRINT`;
delimiter ;;
CREATE PROCEDURE `stp_COM_GUIA_ENTREGA_TOPRINT`(IN `CODIGO` int)
BEGIN
	#Routine body goes here...

SELECT IFNULL(GUIA_REFERENCIA, CONCAT(GUIA_CODIGO, "/", YEAR(GUIA_DATA_ENTREGA))), IFNULL(FAT_ITEM_DESGINATION, ART_DESIGNACAO) AS DESIGNACAO_PRODUTO, MAR_DESCRICAO, CAT_DESCRICAO, IFNULL(GUIA_ITEM_QUANTIDADE_ROLO, 0) AS GUIA_ITEM_QUANTIDADE_ROLO, IFNULL(GUIA_ITEM_QUANT_POR_ROLO, 0) as GUIA_ITEM_QUANT_POR_ROLO,
FAT_ITEM_QUANTIDADE, (GUIA_ITEM_QUANTIDADE_ENTREGUE - FAT_ITEM_QUANTIDADE) AS EXCESSO,  GUIA_ITEM_QUANTIDADE_ENTREGUE,
FAT_NOME_CLIENTE, GUIA_CODIGO_MOTORISTA, FAT_REFERENCIA, FAT_DATA_EMISSAO, "" AS FATURA, GUIA_DATA_ENTREGA, 
ENT_NOME_COMPLETO, GUIA_UPDATED_DATE,  FAT_ITEM_COMENTARIOS
FROM COM_GUIA_ENTREGA_ITEM
INNER JOIN COM_GUIA_ENTREGA ON GUIA_CODIGO = GUIA_ITEM_GUIA_ID
INNER JOIN COM_FATURA_CLIENTE_ITEM ON FAT_ITEM_CODIGO = GUIA_ITEM_CODIGO_ARTIGO
INNER JOIN GER_ARTIGO ON ART_CODIGO = FAT_ITEM_CODIGO_ARTIGO
INNER JOIN GER_CATEGORIA ON CAT_CODIGO = ART_CATEGORIA
LEFT JOIN GER_MARCA ON MAR_CODIGO = ART_MARCA
INNER JOIN COM_FATURA_CLIENTE ON FAT_CODIGO = GUIA_DOCUMENTO_ORIGEM
INNER JOIN REST_FUNCIONARIOS ON GAR_CODIGO = GUIA_CODIGO_FUNCIONARIO
INNER JOIN GER_ENTIDADE ON ENT_CODIGO = GAR_CODIGO_ENTIDADE
WHERE GUIA_ITEM_GUIA_ID = CODIGO;

END
;;
