-- ----------------------------
-- Procedure structure for stp_COM_MAPA_IMPOSTO_SELO
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_COM_MAPA_IMPOSTO_SELO`;
delimiter ;;
CREATE PROCEDURE `stp_COM_MAPA_IMPOSTO_SELO`(IN `EMISSAO_INI` datetime, IN `EMISSAO_TERM` datetime)
BEGIN
	#Routine body goes here...

SELECT
	FAT_CODIGO,
	FAT_DATA_EMISSAO, 
	FAT_REFERENCIA, 
	TER_CODIGO AS FAT_CODIGO_CLIENTE,
	FAT_NOME_CLIENTE,
	(FAT_VALOR_TOTAL - IFNULL(FAT_VALOR_RETENCAO,0)) AS FAT_VALOR_TOTAL,
	(FAT_VALOR_PAGO - FAT_TROCO) AS FAT_VALOR_PAGO, 
	DOC_FORMATO,
	FAT_CREATED_DATE, 
  DOC_CODIGO,
  MOE_SIGLA,
  FAT_CAMBIO,
  FAT_PARCELA,
  FAT_TOTAL_DESCONTOS,	
	IFNULL(FAT_VALOR_RETENCAO,0) AS FAT_VALOR_RETENCAO
	
FROM
	COM_FATURA_CLIENTE C
INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = FAT_CODIGO_DOCUMENTO
INNER JOIN COM_SERIE_DOCUMENTACAO ON SER_CODIGO = FAT_CODIGO_SERIE
LEFT JOIN GER_ENTIDADE ON ENT_CODIGO = FAT_CODIGO_CLIENTE
INNER JOIN FIN_MOEDA ON MOE_CODIGO = FAT_CODIGO_MOEDA
INNER JOIN GER_TERCEIROS ON TER_CODIGO_ENTIDADE = ENT_CODIGO
WHERE (FAT_STATUS = 1 OR FAT_RECTIFIER_DOCUMENT_ID > 0) AND
REC_DATA_EMISSAO BETWEEN EMISSAO_INI AND EMISSAO_TERM
AND FAT_NUMERACAO > 0 AND DOC_FORMATO = "INVOICE_R"
UNION
	SELECT
		REC_CODIGO,
		REC_DATA_EMISSAO, 
		REC_REFERENCIA, 
		TER_CODIGO AS FAT_CODIGO_CLIENTE, 
		REC_NOME_CLIENTE,
		REC_VALOR_TOTAL,
		REC_VALOR_PAGO AS FAT_VALOR_PAGO,
		IFNULL(REC_VALOR_EXCESSO, 0) AS FAT_EXCESSO,
		DOC_FORMATO,
		REC_CREATED_DATE AS FAT_CREATED_DATE, 
    DOC_CODIGO,
    MOE_SIGLA,
    REC_CAMBIO,
		1 AS FAT_PARCELA,
	  REC_DESCONTO_NUMERARIO,
	  0 AS REC_RETENCAO	
	FROM
		COM_RECIBO_CLIENTE C
	LEFT JOIN GER_TERCEIROS ON TER_CODIGO = REC_CODIGO_ENTIDADE
	INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = REC_CODIGO_DOCUMENTO
	INNER JOIN COM_SERIE_DOCUMENTACAO ON SER_CODIGO = REC_CODIGO_SERIE
  INNER JOIN FIN_MOEDA ON MOE_CODIGO = REC_CODIGO_MOEDA
	WHERE REC_DATA_EMISSAO BETWEEN EMISSAO_INI AND EMISSAO_TERM
	AND REC_NUMERACAO > 0 AND REC_STATUS = 1 AND DOC_FORMATO = "RECEIPT_R"

ORDER BY FAT_CREATED_DATE;

END
;;
delimiter ;

