DROP PROCEDURE IF EXISTS `stp_COM_MAPA_IVA_VENDAS_RESUMO`;
delimiter ;;
CREATE PROCEDURE `stp_COM_MAPA_IVA_VENDAS_RESUMO`(IN `PERIODO_INI` datetime,IN `PERIODO_TERM` datetime,IN `COMPANY_ID` int)
BEGIN
	#Routine body goes here...
SELECT IMP_CODIGO, IMP_DESCRICAO, IMP_SIGLA, IMP_VALOR, COUNT(FAT_CODIGO) AS QTD_DOCS, SUM(FAT_ITEM_QUANTIDADE * FAT_ITEM_PRECO) AS MERCADORIA,
(SUM(FAT_ITEM_QUANTIDADE * FAT_ITEM_PRECO)-SUM(FAT_ITEM_VALOR_DESCONTOS)) AS INCIDENCIA, ((SUM(FAT_ITEM_QUANTIDADE * FAT_ITEM_PRECO)-SUM(FAT_ITEM_VALOR_DESCONTOS)) * (IMP_VALOR/100)) AS TOTAL FROM GER_IMPOSTOS
INNER JOIN GER_ARTIGO ON ART_CODIGO_IMPOSTO = IMP_CODIGO 
INNER JOIN COM_FATURA_CLIENTE_ITEM ON FAT_ITEM_CODIGO_ARTIGO = ART_CODIGO 
INNER JOIN COM_FATURA_CLIENTE ON FAT_CODIGO = FAT_ITEM_CODIGO_FATURA
WHERE FAT_ITEM_IMPOSTO > 0 AND FAT_DATA_EMISSAO BETWEEN PERIODO_INI AND PERIODO_TERM
AND FAT_CODIGO_FILIAL = COMPANY_ID
GROUP BY IMP_VALOR;

END
;;
