-- ----------------------------
-- Procedure structure for stp_COM_OBTERARTIGOS_VENDIDOS_ENTRE_DATAS
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_COM_OBTERARTIGOS_VENDIDOS_ENTRE_DATAS`;
delimiter ;;
CREATE PROCEDURE `stp_COM_OBTERARTIGOS_VENDIDOS_ENTRE_DATAS`(IN `FILIAL` int,IN `DATA_INI` date,IN `DATA_FIN` date, WAREHOUSE_ID INT, UTILIZADOR VARCHAR(20))
BEGIN
	#Routine body goes here...
DECLARE v_CON_ LONGTEXT;
DECLARE PERFIL_ID INT DEFAULT 0;
DECLARE UTILIZADOR_ID INT DEFAULT 0;


SELECT UTI_CODIGO_PERFIL, UTI_CODIGO INTO PERFIL_ID, UTILIZADOR_ID FROM SIS_UTILIZADOR WHERE UTI_UTILIZADOR = UTILIZADOR;

SET v_CON_ = CONCAT("
 
SELECT 
CONCAT(UPPER(ART_REFERENCIA), ' - ',ART_DESIGNACAO) AS ART_DESIGNACAO , IFNULL(FAT_ITEM_PRECO_CUSTO, 0) AS FAT_ITEM_PRECO_CUSTO, SUM(FAT_ITEM_QUANTIDADE) AS FAT_ITEM_QUANTIDADE, 
FAT_ITEM_PRECO AS PRECO_VENDA ,  FAT_ITEM_VALOR_DESCONTOS AS DESCONTO, FAT_ITEM_VALOR_IMPOSTOS AS IMPOSTO, SUM(FAT_ITEM_TOTAL) AS VENDA_BRUTA, 
ART_MARGEM_LUCRO, CAT_CODIGO, CAT_CATEGORIA, CAT_DESCRICAO
FROM 
COM_FATURA_CLIENTE_ITEM F
INNER JOIN GER_ARTIGO A ON A.ART_CODIGO=F.FAT_ITEM_CODIGO_ARTIGO
INNER JOIN COM_FATURA_CLIENTE D ON FAT_CODIGO = FAT_ITEM_CODIGO_FATURA
INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = FAT_CODIGO_DOCUMENTO
INNER JOIN GER_CATEGORIA ON CAT_CODIGO = ART_CATEGORIA
WHERE D.FAT_STATUS = 1 AND (FAT_ITEM_STATUS IS NULL OR FAT_ITEM_STATUS=1) AND D.FAT_STATUS_DOCUMENTO = 8 AND D.FAT_CODIGO_FILIAL=",FILIAL," AND 
D.FAT_DATA_EMISSAO  BETWEEN '", DATA_INI, "' AND '", DATA_FIN, "' AND FAT_NUMERACAO > 0
AND DOC_FORMATO IN ('INVOICE_R', 'INVOICE')");

IF(WAREHOUSE_ID > 0) THEN
 SET v_CON_=CONCAT(v_CON_, " AND F.FAT_ITEM_WAREHOUSE_ID = ", WAREHOUSE_ID);
ELSE
 SET v_CON_=CONCAT(v_CON_," AND (ART_MOVIMENTA_STOCK = 0 OR F.FAT_ITEM_WAREHOUSE_ID IN (SELECT WAR_PERM_ARMAZEM_ID FROM GER_ARMAZEM_PERMISSOES
WHERE WAR_PERM_STATUS = 1 AND (WAR_PERM_PERFIL_ID =",PERFIL_ID," OR WAR_PERM_UTILIZADOR_ID = ",UTILIZADOR_ID,")))");
END IF;

SET v_CON_ = CONCAT(v_CON_,"GROUP BY F.FAT_ITEM_CODIGO_ARTIGO, FAT_ITEM_PRECO ORDER BY ART_DESIGNACAO");

#SELECT v_CON_;
SET @stmt_str = v_CON_;
PREPARE stmt FROM @stmt_str;
EXECUTE stmt;
DEALLOCATE PREPARE stmt;
END
;;
delimiter ;

