-- ----------------------------
-- Procedure structure for stp_COM_OBTERLUCROPORARTIGO
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_COM_OBTERLUCROPORARTIGO`;
delimiter ;;
CREATE PROCEDURE `stp_COM_OBTERLUCROPORARTIGO`(IN `FATURA` int, IN `DE` datetime, IN `ATE` datetime)
BEGIN
	#Routine body goes here...
 
IF(FATURA > 0) THEN
SELECT I.FAT_ITEM_CODIGO_FATURA,
A.ART_REFERENCIA,
A.ART_DESIGNACAO,
IFNULL(FAT_ITEM_PRECO_CUSTO, 0) AS FAT_ITEM_PRECO_CUSTO,
A.ART_PCM_CALCULADO,
A.ART_PRECO_CUSTO,  
I.FAT_ITEM_QUANTIDADE,
I.FAT_ITEM_PRECO AS VENDA,
((I.FAT_ITEM_PRECO*I.FAT_ITEM_QUANTIDADE)-(A.ART_PRECO_CUSTO*I.FAT_ITEM_QUANTIDADE)) AS LUCRO
FROM COM_FATURA_CLIENTE_ITEM I
INNER JOIN GER_ARTIGO A ON A.ART_CODIGO=I.FAT_ITEM_CODIGO_ARTIGO
INNER JOIN COM_FATURA_CLIENTE ON FAT_CODIGO = FAT_ITEM_CODIGO_FATURA
WHERE I.FAT_ITEM_CODIGO_FATURA=FATURA ORDER BY FAT_ITEM_CODIGO_ARTIGO;

ELSE
SELECT I.FAT_ITEM_CODIGO_FATURA,
A.ART_REFERENCIA,
A.ART_DESIGNACAO,
IFNULL(FAT_ITEM_PRECO_CUSTO, 0) AS FAT_ITEM_PRECO_CUSTO,
A.ART_PCM_CALCULADO,
A.ART_PRECO_CUSTO,  
SUM(I.FAT_ITEM_QUANTIDADE) AS FAT_ITEM_QUANTIDADE,
I.FAT_ITEM_PRECO AS VENDA,
((I.FAT_ITEM_PRECO*SUM(I.FAT_ITEM_QUANTIDADE))-(A.ART_PRECO_CUSTO*SUM(I.FAT_ITEM_QUANTIDADE))) AS LUCRO
FROM COM_FATURA_CLIENTE_ITEM I
INNER JOIN GER_ARTIGO A ON A.ART_CODIGO=I.FAT_ITEM_CODIGO_ARTIGO
INNER JOIN COM_FATURA_CLIENTE ON FAT_CODIGO = FAT_ITEM_CODIGO_FATURA
WHERE FAT_DATA_EMISSAO BETWEEN DE AND ATE GROUP BY FAT_ITEM_CODIGO_ARTIGO  ORDER BY FAT_ITEM_CODIGO_ARTIGO;
END IF;
END
;;
