-- ----------------------------
-- Procedure structure for stp_COM_PAGAMENTO_FORNECEDOR_ADICIONAR
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_COM_PAGAMENTO_FORNECEDOR_ADICIONAR`;
delimiter ;;
CREATE PROCEDURE `stp_COM_PAGAMENTO_FORNECEDOR_ADICIONAR`(IN `CODIGO` int,IN `FILIAL` int,IN `DOCUMENTO` int,IN `SERIE` int,IN `FORNECEDOR` int,IN `EMISSAO` datetime,IN `NUMERACAO` int,IN `REFERENCIA` varchar(20),IN `MOEDA` int,IN `CAMBIO` decimal(18,5),IN `VALOR_TOTAL` decimal(18,5),IN `VALOR_PAGO` decimal(18,5),IN `NOME_FORNECEDOR` varchar(120),IN `CONTRIBUINTE` varchar(18),IN `OBSERVACOES` varchar(255),IN `ANULADO` bit,IN `UTILIZADOR` varchar(20), IN `DESCONTO` decimal(18,5), OUT `LSTID` int)
BEGIN
	#Routine body goes here...
DECLARE DOCUMENT_SIMBOL VARCHAR(20) DEFAULT "";
DECLARE DOCUMENT_NUMBER INT DEFAULT 0;
DECLARE ANO_FATURACAO INT DEFAULT 0;
DECLARE PREFIXO VARCHAR(12);

IF(CODIGO <=0) THEN
SELECT SER_DESCRICAO INTO REFERENCIA FROM COM_SERIE_DOCUMENTACAO WHERE SER_CODIGO = SERIE;
SET NUMERACAO = 0;

SELECT TER_CODIGO INTO FORNECEDOR FROM GER_TERCEIROS WHERE TER_CODIGO_ENTIDADE = FORNECEDOR;

INSERT INTO COM_PAGAMENTO_FORNECEDOR (REC_CODIGO,
REC_CODIGO_FILIAL,
REC_CODIGO_DOCUMENTO,
REC_CODIGO_SERIE,
REC_CODIGO_ENTIDADE,
REC_DATA_EMISSAO,
REC_NUMERACAO,
REC_REFERENCIA,
REC_CODIGO_MOEDA,
REC_CAMBIO,
REC_VALOR_TOTAL,
REC_VALOR_PAGO,
REC_NOME_FORNECEDOR,
REC_CONTRIBUINTE,
REC_OBSERVACOES,
REC_STATUS,
REC_MOTIVO_ANULACAO,
REC_CREATED_BY,
REC_CREATED_DATE,
REC_UPDATED_BY,
REC_UPDATED_DATE,
REC_BARCODE,
REC_DOC_FROM_TYPE,
REC_DOC_FROM_NUMBER,
REC_DESCONTO
) VALUES (DEFAULT, FILIAL, DOCUMENTO, SERIE, FORNECEDOR, EMISSAO, NUMERACAO, REFERENCIA, MOEDA, CAMBIO, VALOR_TOTAL, 
VALOR_PAGO, NOME_FORNECEDOR, CONTRIBUINTE, OBSERVACOES, 1, NULL, UTILIZADOR, NOW(), UTILIZADOR, NOW(), '', NULL, NULL, DESCONTO);
SELECT LAST_INSERT_ID() INTO CODIGO;
 
END IF;

IF(NUMERACAO IS NULL OR NUMERACAO =0) THEN
  SELECT REC_NUMERACAO INTO NUMERACAO FROM COM_PAGAMENTO_FORNECEDOR WHERE REC_CODIGO = CODIGO;           
END IF;

IF(NUMERACAO IS NULL OR NUMERACAO < 1) THEN
			 SELECT REC_REFERENCIA INTO REFERENCIA FROM COM_PAGAMENTO_FORNECEDOR WHERE REC_CODIGO = CODIGO;
			 SELECT DOC_SIGLA INTO DOCUMENT_SIMBOL FROM COM_DOCUMENTO WHERE DOC_CODIGO = DOCUMENTO;
			 SELECT SER_NUMERACAO, SER_ANO INTO DOCUMENT_NUMBER, ANO_FATURACAO FROM COM_SERIE_DOCUMENTACAO WHERE SER_CODIGO = SERIE;

       SET DOCUMENT_NUMBER = DOCUMENT_NUMBER+1;
        IF(DOCUMENT_NUMBER < 10) THEN 
          SET PREFIXO = CONCAT("00000",DOCUMENT_NUMBER); 
        ELSE IF(DOCUMENT_NUMBER >= 10 AND DOCUMENT_NUMBER <100) THEN 
          SET PREFIXO = CONCAT("0000",DOCUMENT_NUMBER); 
        ELSE IF(DOCUMENT_NUMBER >= 100 AND DOCUMENT_NUMBER <1000) THEN 
          SET PREFIXO = CONCAT("000",DOCUMENT_NUMBER); 
        ELSE IF(DOCUMENT_NUMBER >= 1000 AND DOCUMENT_NUMBER <10000) THEN 
          SET PREFIXO = CONCAT("00",DOCUMENT_NUMBER); 
        ELSE IF(DOCUMENT_NUMBER >= 10000 AND DOCUMENT_NUMBER <100000) THEN 
          SET PREFIXO = CONCAT("0",DOCUMENT_NUMBER); 
        ELSE IF(DOCUMENT_NUMBER >= 100000) THEN 
          SET PREFIXO = DOCUMENT_NUMBER; 
        END IF;
        END IF;
        END IF;
        END IF;
        END IF;
        END IF;
			 SET REFERENCIA = CONCAT(DOCUMENT_SIMBOL," ",ANO_FATURACAO,"/",PREFIXO); 

			 UPDATE COM_PAGAMENTO_FORNECEDOR SET
			 REC_NUMERACAO = DOCUMENT_NUMBER,
			 REC_REFERENCIA = REFERENCIA
			 WHERE REC_CODIGO = CODIGO;

			 UPDATE COM_SERIE_DOCUMENTACAO SET
			 SER_NUMERACAO = SER_NUMERACAO + 1
			 WHERE SER_CODIGO = SERIE;
END IF;


SET LSTID = CODIGO;

END
;;
