delimiter ;;
CREATE PROCEDURE `stp_COM_PAGAMENTO_FORNECEDOR_OBTERPORFILTRO`(IN FILIAL INT, IN EMISSAO_INI DATE, IN EMISSAO_TERM DATE, REFERENCIA VARCHAR(12), FORNECEDOR VARCHAR(120), DOCUMENTO INT,  SITUACAO INT, in ENTIDADE INT, TIPO VARCHAR(1), IN SERIE INT)
BEGIN
	#Routine body goes here...
DECLARE v_CON_ LONGTEXT;
 
SET v_CON_ = CONCAT("

SELECT 
 REC_CODIGO, 
 REC_REFERENCIA, 
 REC_DATA_EMISSAO, 
 DOC_DESCRICAO, 
 REC_NOME_FORNECEDOR,   
 REC_VALOR_TOTAL,
 REC_VALOR_PAGO
FROM 
	COM_PAGAMENTO_FORNECEDOR C
LEFT JOIN GER_TERCEIROS ON TER_CODIGO_ENTIDADE = REC_CODIGO_ENTIDADE
INNER JOIN COM_DOCUMENTO on DOC_CODIGO = REC_CODIGO_DOCUMENTO

WHERE C.REC_CODIGO_FILIAL=",FILIAL, " AND REC_REFERENCIA LIKE '%",REFERENCIA,"%' AND 
REC_NOME_FORNECEDOR LIKE '%",FORNECEDOR,"%'");
IF(SITUACAO > 0) THEN
SET v_CON_ = CONCAT(v_CON_," AND REC_STATUS=1");
END IF;

IF(EMISSAO_INI <>'' AND EMISSAO_TERM <>'') THEN
  SET v_CON_ = CONCAT(v_CON_," AND REC_DATA_EMISSAO BETWEEN '",EMISSAO_INI, "' AND '", EMISSAO_TERM, "'");
ELSE IF(EMISSAO_INI IS NOT NULL AND EMISSAO_TERM IS NULL) THEN
  SET v_CON_ = CONCAT(v_CON_," AND REC_DATA_EMISSAO >= ",EMISSAO_INI);
ELSE IF(EMISSAO_INI IS NULL AND EMISSAO_TERM IS NOT NULL) THEN
  SET v_CON_ = CONCAT(v_CON_," AND REC_DATA_EMISSAO <= ", EMISSAO_TERM);
END IF;
END IF;
END IF;

IF(TIPO!='' AND TIPO !='A') THEN
SET v_CON_ = CONCAT(v_CON_," AND TER_TIPO = '",TIPO,"' ");
END IF; 

IF(DOCUMENTO > 0) THEN
SET v_CON_=CONCAT(v_CON_," AND REC_CODIGO_DOCUMENTO = ", DOCUMENTO);
END IF;

IF(SERIE > 0) THEN
SET v_CON_=CONCAT(v_CON_," AND REC_CODIGO_SERIE = ", SERIE);
END IF;

IF(SITUACAO > 0) THEN
 SET v_CON_=CONCAT(v_CON_," AND REC_STATUS =1 ");
END IF;


IF(ENTIDADE > 0) THEN
SET v_CON_=CONCAT(v_CON_," AND REC_CODIGO_ENTIDADE = ", ENTIDADE); 
END IF;


SET v_CON_ = CONCAT(v_CON_," ORDER BY REC_REFERENCIA, REC_DATA_EMISSAO");
#SELECT v_CON_;
SET @stmt_str = v_CON_;
PREPARE stmt FROM @stmt_str;
EXECUTE stmt;
DEALLOCATE PREPARE stmt;

END
;;
