S.POS_STA_VALOR_INICIAL, (S.POS_STA_VALOR_INICIAL + fn_Saldo_Caixa(S.POS_STA_DATA, S.POS_STA_DATA, UTI_UTILIZADOR, FILIAL)) 
AS VALOR_FINAL, S.POS_STA_FECHO, UTI_UTILIZADOR, fn_Saldo_Caixa(S.POS_STA_DATA, S.POS_STA_DATA, UTI_UTILIZADOR, FILIAL)AS MOVIMENTO_POS, TUR_BEGIN, TUR_END, POS_STA_TURNO
