IFNULL(POS_DEFAULT_WAREHOUSE, -1) AS POS_DEFAULT_WAREHOUSE, POS_DEFAULT_DOCUMENT_SERIE, POS_DEFAULT_CUSTOMER, POS_DESIGNACAO, ENT_NOME_COMPLETO, 
UPPER(UTI_NOME)AS UTI_NOME, D.SER_CODIGO_DOCUMENTO, DOC_DESCRICAO, ARM_CODIGO_FILIAL, ARM_DESCRICAO, POS_PAYMENT_TERMS_ID,
POS_PAYMENT_METHOD_ID, POS_VENDEDOR_ID, POS_PVP_ID, POS_FUNDO_MANEIO, POS_ALLOW_CALENDAR, POS_SUSPENSE_SALES, 
POS_CASH_REFUND_SERIE_ID, POS_CREDIT_REFUND_SERIE_ID, CD.SER_CODIGO_DOCUMENTO AS CASH_REFUND_DOCUMENT, 
CR.SER_CODIGO_DOCUMENTO AS CREDIT_REFUND_DOCUMENT, POS_PIN_CODE
FROM COM_POS P
INNER JOIN SIS_UTILIZADOR ON UTI_UTILIZADOR = POS_DESCRICAO
LEFT JOIN REST_FUNCIONARIOS ON GAR_UTILIZADOR = POS_DESCRICAO
LEFT JOIN GER_ARMAZEM ON ARM_CODIGO = POS_DEFAULT_WAREHOUSE 
LEFT JOIN GER_ENTIDADE ON ENT_CODIGO = POS_DEFAULT_CUSTOMER
LEFT JOIN COM_SERIE_DOCUMENTACAO D ON D.SER_CODIGO = POS_DEFAULT_DOCUMENT_SERIE
LEFT JOIN COM_DOCUMENTO ON DOC_CODIGO = D.SER_CODIGO_DOCUMENTO
LEFT JOIN COM_SERIE_DOCUMENTACAO CD ON CD.SER_CODIGO = POS_CASH_REFUND_SERIE_ID
LEFT JOIN COM_SERIE_DOCUMENTACAO CR  ON CR.SER_CODIGO = POS_CREDIT_REFUND_SERIE_ID
INNER JOIN SIS_UTILIZADOR_FILIAL ON UTI_FILIAL_UTILIZADOR = POS_DESCRICAO
WHERE UTI_ESTADO = "A" AND POS_STATUS = 1 AND UTI_FILIAL_CODIGO_FILIAL = FILIAL;

ELSE

SELECT POS_CODIGO, POS_DESCRICAO, POS_SIGLA, POS_STATUS, IFNULL(GAR_CODIGO, -1) AS EMPLOYEE_ID,
POS_DEFAULT_WAREHOUSE, POS_DEFAULT_DOCUMENT_SERIE, POS_DEFAULT_CUSTOMER, POS_DESIGNACAO, ENT_NOME_COMPLETO, 
UPPER(UTI_NOME)AS UTI_NOME, D.SER_CODIGO_DOCUMENTO, DOC_DESCRICAO, ARM_CODIGO_FILIAL, ARM_DESCRICAO, POS_PAYMENT_TERMS_ID,
POS_PAYMENT_METHOD_ID, POS_VENDEDOR_ID, POS_PVP_ID, POS_FUNDO_MANEIO, POS_ALLOW_CALENDAR, POS_SUSPENSE_SALES, 
POS_CASH_REFUND_SERIE_ID, POS_CREDIT_REFUND_SERIE_ID, CD.SER_CODIGO_DOCUMENTO AS CASH_REFUND_DOCUMENT, 
CR.SER_CODIGO_DOCUMENTO AS CREDIT_REFUND_DOCUMENT, POS_PIN_CODE 
FROM COM_POS P
INNER JOIN SIS_UTILIZADOR ON UTI_UTILIZADOR = POS_DESCRICAO
LEFT JOIN REST_FUNCIONARIOS ON GAR_UTILIZADOR = POS_DESCRICAO
LEFT JOIN GER_ARMAZEM ON ARM_CODIGO = POS_DEFAULT_WAREHOUSE 
LEFT JOIN GER_ENTIDADE ON ENT_CODIGO = POS_DEFAULT_CUSTOMER
LEFT JOIN COM_SERIE_DOCUMENTACAO D ON D.SER_CODIGO = POS_DEFAULT_DOCUMENT_SERIE
LEFT JOIN COM_DOCUMENTO ON DOC_CODIGO = D.SER_CODIGO_DOCUMENTO
LEFT JOIN COM_SERIE_DOCUMENTACAO CD ON CD.SER_CODIGO = POS_CASH_REFUND_SERIE_ID
LEFT JOIN COM_SERIE_DOCUMENTACAO CR  ON CR.SER_CODIGO = POS_CREDIT_REFUND_SERIE_ID
INNER JOIN SIS_UTILIZADOR_FILIAL ON UTI_FILIAL_UTILIZADOR = POS_DESCRICAO
WHERE UTI_ESTADO = "A" AND POS_STATUS = 1;

END IF;

END
