delimiter ;;
CREATE PROCEDURE `stp_COM_POS_SALDO_DIA`(IN `UTILIZADOR` VARCHAR(20),IN DATA_ANTERIOR DATE)
BEGIN
	#Routine body goes here...
SET @RECEBIDO:=(SELECT IFNULL(SUM(C.MOV_VALOR),0) FROM fin_movimento_caixa C WHERE C.MOV_TRANSACAO='E' 
AND C.MOV_UTILIZADOR=UTILIZADOR AND C.MOV_DATA=DATA_ANTERIOR);
SET @TROCO:=(SELECT IFNULL(SUM(C.MOV_VALOR),0) FROM fin_movimento_caixa C WHERE C.MOV_TRANSACAO='S' 
AND C.MOV_UTILIZADOR=UTILIZADOR AND C.MOV_DATA=DATA_ANTERIOR);
SET @TOTAL:=(@RECEBIDO-@TROCO);
SELECT @TOTAL AS MOVIMENTO_CAIXA;
END
