CREATE PROCEDURE `stp_COM_POS_STATUS_EXTRACT`(IN `POS_ID` int)
BEGIN
	#Routine body goes here...
select MOV_CODIGO, DOC_DESCRICAO,  FAT_REFERENCIA, REC_REFERENCIA, (CASE WHEN MOV_TRANSACAO='E' THEN
		  MOV_VALOR 
		  WHEN (MOV_TRANSACAO='S' AND MOV_VALOR > 0 -MOV_TRANSACAO) THEN (MOV_VALOR *(-1))
     ELSE 0 
    END) AS MOV_VALOR, MOV_DATA_LANCAMANTO, IFNULL(FAT_NOME_CLIENTE, REC_NOME_CLIENTE) AS ENTIDADE, (CASE WHEN FAT_REFERENCIA IS NOT NULL THEN
 CONCAT("../Comercial/Reports/PreviewReceipt?pDocID=",FAT_CODIGO) WHEN (REC_REFERENCIA IS NOT NULL) THEN  
 CONCAT("../Comercial/Reports/PreviewInvoice?pDocID=",REC_CODIGO)
 END) AS URL_DOCUMENT, POS_STA_CODIGO, CONCAT(POS_DESCRICAO," - ", UTI_NOME) AS POSTO, POS_STA_VALOR_INICIAL, PAG_DESCRICAO from com_pos_status
LEFT JOIN com_pos_movimento M ON  M.POS_MOV_CODIGO_POS = POS_STA_CODIGO
LEFT JOIN FIN_MOVIMENTO_CAIXA ON MOV_CODIGO = M.POS_MOV_CODIGO_MOVIMENTO
INNER JOIN GER_MEIO_PAGAMENTO ON PAG_CODIGO = MOV_MEIO_PAGAMENTO
LEFT JOIN COM_FATURA_CLIENTE ON FAT_CODIGO = MOV_DOCUMENT_ID
LEFT JOIN COM_RECIBO_CLIENTE ON REC_CODIGO = MOV_DOCUMENT_ID
LEFT JOIN COM_DOCUMENTO F ON F.DOC_CODIGO = MOV_DOCUMENT_TYPE
INNER JOIN COM_POS ON POS_CODIGO = POS_STA_POSTO
INNER JOIN SIS_UTILIZADOR ON UTI_UTILIZADOR = POS_DESCRICAO


WHERE POS_STA_CODIGO=POS_ID;

END
;;
