  POS_STA_POSTO = POS AND POS_STA_UTILIZADOR = UTILIZADOR AND POS_STA_DATA = CURDATE() 
  AND POS_STA_TURNO = TURNO;
  IF(CODIGO > 0) THEN
     SELECT POS_STA_VALOR_FINAL INTO SALDO_INICIAL FROM COM_POS_STATUS WHERE POS_STA_CODIGO = CODIGO;
