   POS_STA_ABERTURA, POS_STA_CODIGO_FILIAL, POS_IP)
   VALUES(POS, UTILIZADOR, CURDATE(), SALDO_INICIAL, TURNO, NOW(), FILIAL, IP);   

  END IF;
  
ELSE 
	UPDATE COM_POS_STATUS SET
	POS_STA_FECHO = NOW(),
	POS_STA_VALOR_FINAL = SALDO_FINAL
	WHERE POS_STA_CODIGO = CODIGO; 
END IF;
