delimiter ;;
CREATE PROCEDURE `stp_COM_POS_STATUS_SESSOES`(IN FILIAL INT, IN `SESSION_DATE` date, IN POS_ID int, UTILIZADOR VARCHAR(20))
BEGIN
	#Routine body goes here...
DECLARE vCONSULTA LONGTEXT;

SET vCONSULTA = CONCAT("
SELECT POS_STA_CODIGO, UTI_NOME, POS_STA_DATA, POS_STA_ABERTURA, POS_STA_VALOR_INICIAL, POS_STA_FECHO,  IFNULL(SUM(
(CASE WHEN MOV_TRANSACAO='E' THEN
		  MOV_VALOR 
		  WHEN (MOV_TRANSACAO='S' AND MOV_VALOR > 0 -MOV_TRANSACAO) THEN (MOV_VALOR *(-1))
     ELSE 0 
    END)
),0) AS TOTAL FROM FIN_MOVIMENTO_CAIXA C 
INNER JOIN COM_POS_MOVIMENTO ON POS_MOV_CODIGO_MOVIMENTO = MOV_CODIGO 
INNER JOIN COM_POS_STATUS ON POS_STA_CODIGO = POS_MOV_CODIGO_POS
INNER JOIN COM_POS ON POS_CODIGO = POS_STA_POSTO
INNER JOIN SIS_UTILIZADOR ON UTI_UTILIZADOR = POS_DESCRICAO 
WHERE MOV_CODIGO_FILIAL =", FILIAL); 
IF(SESSION_DATE IS NOT NULL) THEN 
	SET vCONSULTA=CONCAT(vCONSULTA," AND POS_STA_DATA >= '", SESSION_DATE,"'");
END IF;

IF(POS_ID > 0) THEN
SET vCONSULTA=CONCAT(vCONSULTA," AND POS_CODIGO = ", POS_ID);
END IF;

IF(UTILIZADOR IS NOT NULL AND UTILIZADOR!="") THEN 
	SET vCONSULTA=CONCAT(vCONSULTA," AND UTI_UTILIZADOR='", UTILIZADOR,"'");
END IF;

SET vCONSULTA=CONCAT(vCONSULTA, " AND POS_STA_FECHO IS NULL GROUP BY POS_STA_CODIGO"); 



SET @stmt_str = vCONSULTA;
PREPARE stmt FROM @stmt_str;
EXECUTE stmt;
DEALLOCATE PREPARE stmt;

END
;;
delimiter ;

