-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_COM_RECIBO_CLIENTE_ADICIONAR`;
delimiter ;;
CREATE PROCEDURE `stp_COM_RECIBO_CLIENTE_ADICIONAR`(IN `CODIGO` int,IN `FILIAL` int,IN `DOCUMENTO` int,IN `SERIE` int,IN `CLIENTE` int,IN `EMISSAO` datetime,IN `NUMERACAO` int,IN `REFERENCIA` varchar(20),IN `MOEDA` int,IN `CAMBIO` decimal(18,5),IN `VALOR_TOTAL` decimal(18,5),IN `VALOR_PAGO` decimal(18,5),IN `NOME_CLIENTE` varchar(120),IN `CONTRIBUINTE` varchar(18),IN `OBSERVACOES` varchar(255),IN `ANULADO` smallint,IN `UTILIZADOR` varchar(20), DOCUMENT_FROM_ID INT, IN `DESCONTO` decimal(18,5), DESCONTO_NUMERARIO DECIMAL(18,5), TAX_ID int, VALOR_EXCESSO decimal(18,5), OUT `LSTID` int)
BEGIN
	#Routine body goes here...
DECLARE DOCUMENT_SIMBOL VARCHAR(5) DEFAULT "";
DECLARE DOCUMENT_NUMBER INT DEFAULT 0;
DECLARE ANO_FATURACAO INT DEFAULT 0;
DECLARE PREFIXO VARCHAR(12);

IF(CODIGO <=0) THEN
SELECT SER_DESCRICAO INTO REFERENCIA FROM COM_SERIE_DOCUMENTACAO WHERE SER_CODIGO = SERIE;
SET NUMERACAO = 0;

SELECT TER_CODIGO INTO CLIENTE FROM GER_TERCEIROS WHERE TER_CODIGO_ENTIDADE = CLIENTE;

INSERT INTO COM_RECIBO_CLIENTE (REC_CODIGO,
REC_CODIGO_FILIAL,
REC_CODIGO_DOCUMENTO,
REC_CODIGO_SERIE,
REC_CODIGO_ENTIDADE,
REC_DATA_EMISSAO,
REC_NUMERACAO,
REC_REFERENCIA,
REC_CODIGO_MOEDA,
REC_CAMBIO,
REC_VALOR_TOTAL,
REC_VALOR_PAGO,
REC_NOME_CLIENTE,
REC_CONTRIBUINTE,
REC_OBSERVACOES,
REC_STATUS, 
REC_CREATED_BY,
REC_CREATED_DATE,
REC_UPDATED_BY,
REC_UPDATED_DATE, 
REC_DOCUMENT_FROM_ID,
REC_DESCONTO,
REC_DESCONTO_NUMERARIO,
REC_TAX_ID, 
REC_VALOR_EXCESSO
) VALUES (DEFAULT, FILIAL, DOCUMENTO, SERIE, CLIENTE, EMISSAO, NUMERACAO, REFERENCIA, MOEDA, CAMBIO, VALOR_TOTAL, 
VALOR_PAGO, NOME_CLIENTE, CONTRIBUINTE, OBSERVACOES, 1, UTILIZADOR, NOW(), UTILIZADOR, NOW(), DOCUMENT_FROM_ID, DESCONTO, DESCONTO_NUMERARIO, TAX_ID, VALOR_EXCESSO);
SELECT LAST_INSERT_ID() INTO CODIGO;
 
END IF;

IF(NUMERACAO IS NULL OR NUMERACAO =0) THEN
  SELECT REC_NUMERACAO INTO NUMERACAO FROM COM_RECIBO_CLIENTE WHERE REC_CODIGO = CODIGO;           
END IF;

IF(NUMERACAO IS NULL OR NUMERACAO < 1) THEN
			 SELECT REC_REFERENCIA INTO REFERENCIA FROM COM_RECIBO_CLIENTE WHERE REC_CODIGO = CODIGO;
			 SELECT DOC_SIGLA INTO DOCUMENT_SIMBOL FROM COM_DOCUMENTO WHERE DOC_CODIGO = DOCUMENTO;
			 SELECT SER_NUMERACAO, SER_ANO INTO DOCUMENT_NUMBER, ANO_FATURACAO FROM COM_SERIE_DOCUMENTACAO WHERE SER_CODIGO = SERIE;

       SET DOCUMENT_NUMBER = DOCUMENT_NUMBER+1;
        IF(DOCUMENT_NUMBER < 10) THEN 
          SET PREFIXO = CONCAT("00000",DOCUMENT_NUMBER); 
        ELSE IF(DOCUMENT_NUMBER >= 10 AND DOCUMENT_NUMBER <100) THEN 
          SET PREFIXO = CONCAT("0000",DOCUMENT_NUMBER); 
        ELSE IF(DOCUMENT_NUMBER >= 100 AND DOCUMENT_NUMBER <1000) THEN 
          SET PREFIXO = CONCAT("000",DOCUMENT_NUMBER); 
        ELSE IF(DOCUMENT_NUMBER >= 1000 AND DOCUMENT_NUMBER <10000) THEN 
          SET PREFIXO = CONCAT("00",DOCUMENT_NUMBER); 
        ELSE IF(DOCUMENT_NUMBER >= 10000 AND DOCUMENT_NUMBER <100000) THEN 
          SET PREFIXO = CONCAT("0",DOCUMENT_NUMBER); 
        ELSE IF(DOCUMENT_NUMBER >= 100000) THEN 
          SET PREFIXO = DOCUMENT_NUMBER; 
        END IF;
        END IF;
        END IF;
        END IF;
        END IF;
        END IF;
			 SET REFERENCIA = CONCAT(DOCUMENT_SIMBOL," ",ANO_FATURACAO,"/",PREFIXO); 

			 UPDATE COM_RECIBO_CLIENTE SET
			 REC_NUMERACAO = DOCUMENT_NUMBER,
			 REC_REFERENCIA = REFERENCIA
			 WHERE REC_CODIGO = CODIGO;

			 UPDATE COM_SERIE_DOCUMENTACAO SET
			 SER_NUMERACAO = SER_NUMERACAO + 1
			 WHERE SER_CODIGO = SERIE;
END IF;


SET LSTID = CODIGO;

END
;;
delimiter ;

-- ----------------------------
-- Procedure structure for stp_COM_RECIBO_CLIENTE_ANULAR
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_COM_RECIBO_CLIENTE_ANULAR`;
delimiter ;;
CREATE PROCEDURE `stp_COM_RECIBO_CLIENTE_ANULAR`(IN `CODIGO` int, IN `UTILIZADOR` varchar(20), MOTIVO VARCHAR(120))
BEGIN
	#Routine body goes here...
DECLARE RECIBOS INT DEFAULT 0;
DECLARE INVOICE_ID INT DEFAULT 0;
DECLARE PAID DECIMAL(18,2) DEFAULT 0; 
DECLARE TOTAL_DOCUMENTO DECIMAL(18,2) DEFAULT 0;
DECLARE TOTAL_PAGO DECIMAL(18,2) DEFAULT 0;
DECLARE REC_DOC_TYPE INT DEFAULT 0;



SELECT COUNT(REC_DOC_CODIGO) INTO RECIBOS FROM COM_RECIBO_CLIENTE_DOCUMENTOS WHERE REC_DOC_RECIBO_ID = CODIGO AND REC_DOC_ANULADO =1;
WHILE RECIBOS > 0 DO
 #SET RECIBOS = RECIBOS -1;
 SELECT REC_DOC_DOCUMENTO_ID, REC_DOC_VALOR_LIQUIDADO INTO INVOICE_ID, PAID FROM COM_RECIBO_CLIENTE_DOCUMENTOS WHERE REC_DOC_RECIBO_ID = CODIGO AND REC_DOC_ANULADO =1 LIMIT 1;
 
 UPDATE COM_RECIBO_CLIENTE_DOCUMENTOS SET 
 REC_DOC_ANULADO =0 WHERE REC_DOC_RECIBO_ID = CODIGO
 AND REC_DOC_DOCUMENTO_ID = INVOICE_ID;
  
 UPDATE COM_FATURA_CLIENTE SET FAT_VALOR_PAGO = FAT_VALOR_PAGO - PAID
 WHERE FAT_CODIGO = INVOICE_ID;
 
 SELECT FAT_VALOR_TOTAL, FAT_VALOR_PAGO INTO TOTAL_DOCUMENTO, TOTAL_PAGO FROM COM_FATURA_CLIENTE
 WHERE FAT_CODIGO = INVOICE_ID;
 
 IF(TOTAL_DOCUMENTO > TOTAL_PAGO) THEN
   UPDATE COM_FATURA_CLIENTE SET 
	 FAT_STATUS_PAGAMENTO = 1,
	 FAT_VALOR_SALDO = TOTAL_DOCUMENTO - TOTAL_PAGO
	 WHERE FAT_CODIGO = INVOICE_ID;
 END IF;
SELECT COUNT(REC_DOC_CODIGO) INTO RECIBOS FROM COM_RECIBO_CLIENTE_DOCUMENTOS WHERE REC_DOC_RECIBO_ID = CODIGO AND REC_DOC_ANULADO =1; 
END WHILE;

UPDATE COM_RECIBO_CLIENTE SET
REC_STATUS = 0,
REC_MOTIVO_ANULACAO = MOTIVO,
REC_UPDATED_BY = UTILIZADOR,
REC_UPDATED_DATE = NOW(),
REC_ABORTED_BY = UTILIZADOR,
REC_ABORTED_DATE = NOW()
WHERE REC_CODIGO = CODIGO;

SELECT REC_CODIGO_DOCUMENTO INTO REC_DOC_TYPE FROM COM_RECIBO_CLIENTE WHERE REC_CODIGO = CODIGO;

UPDATE FIN_MOVIMENTO_CAIXA SET 
MOV_STATUS =0 WHERE 
MOV_DOCUMENT_TYPE = REC_DOC_TYPE
AND MOV_DOCUMENT_ID = CODIGO;

END
;;
delimiter ;

-- ----------------------------
-- Procedure structure for stp_COM_RECIBO_CLIENTE_DOCUMENTO_LIQUIDAR
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_COM_RECIBO_CLIENTE_DOCUMENTO_LIQUIDAR`;
delimiter ;;
CREATE PROCEDURE `stp_COM_RECIBO_CLIENTE_DOCUMENTO_LIQUIDAR`(IN `CODIGO` int,IN `ORDEM` int,IN `RECIBO` int,IN `DOCUMENTO_TYPE` int,IN `DOCUMENTO_ID` int,IN `NUMERACAO` int,IN `REFERENCIA` varchar(14),IN `VALOR` decimal,IN `LIQUIDADO` decimal(18,5),IN `PENDENTE` decimal(18,5),IN `DESCONTO` decimal(18,5),IN `JUROS` decimal(18,5),IN `UTILIZADOR` varchar(20), DOCUMENT_STATUS INT)
BEGIN
	#Routine body goes here...
IF(DOCUMENTO_TYPE IS NULL OR DOCUMENTO_TYPE <=0) THEN
SELECT FAT_CODIGO_DOCUMENTO, FAT_NUMERACAO  INTO DOCUMENTO_TYPE, NUMERACAO FROM COM_FATURA_CLIENTE WHERE FAT_CODIGO = DOCUMENTO_ID;

END IF; 
IF(LIQUIDADO > 0) THEN
IF(CODIGO <=0) THEN
SET PENDENTE = VALOR - LIQUIDADO;

IF(PENDENTE < 0) THEN
SET PENDENTE =0;
END IF;

 INSERT INTO COM_RECIBO_CLIENTE_DOCUMENTOS VALUES(DEFAULT, ORDEM, RECIBO, DOCUMENTO_TYPE, DOCUMENTO_ID, REFERENCIA, NUMERACAO, 
 VALOR, LIQUIDADO, PENDENTE, DESCONTO, JUROS, DOCUMENT_STATUS, UTILIZADOR, NOW(), UTILIZADOR, NOW());

   UPDATE COM_FATURA_CLIENTE SET
   FAT_VALOR_PAGO = FAT_VALOR_PAGO + LIQUIDADO,
   FAT_VALOR_SALDO = FAT_VALOR_TOTAL - (FAT_VALOR_PAGO + LIQUIDADO)
   WHERE FAT_CODIGO = DOCUMENTO_ID;


	SELECT FAT_VALOR_TOTAL, FAT_VALOR_PAGO INTO VALOR, LIQUIDADO FROM COM_FATURA_CLIENTE 
	WHERE FAT_CODIGO = DOCUMENTO_ID;

	IF(LIQUIDADO >= VALOR) THEN
		UPDATE COM_FATURA_CLIENTE SET
		FAT_STATUS_DOCUMENTO =8, FAT_STATUS_PAGAMENTO =3
		WHERE FAT_CODIGO = DOCUMENTO_ID;
	END IF;

 
END IF;
END IF;

END
;;
delimiter ;

-- ----------------------------
-- Procedure structure for stp_COM_RECIBO_CLIENTE_OBTERPORFILTRO
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_COM_RECIBO_CLIENTE_OBTERPORFILTRO`;
delimiter ;;
CREATE PROCEDURE `stp_COM_RECIBO_CLIENTE_OBTERPORFILTRO`(IN FILIAL INT, IN EMISSAO_INI DATE, IN EMISSAO_TERM DATE, REFERENCIA VARCHAR(12), CLIENTE VARCHAR(120), DOCUMENTO INT,  SITUACAO INT, in ENTIDADE INT, TIPO VARCHAR(1), IN SERIE INT)
BEGIN
	#Routine body goes here...
DECLARE v_CON_ LONGTEXT;
 
SET v_CON_ = CONCAT("

 SELECT 
 REC_CODIGO, 
 REC_REFERENCIA, 
 REC_DATA_EMISSAO, 
 DOC_DESCRICAO, 
 REC_NOME_CLIENTE,   
 REC_VALOR_TOTAL,
 REC_VALOR_PAGO,
 IFNULL(REC_VALOR_EXCESSO, 0) AS REC_VALOR_EXCESSO
FROM 
	COM_RECIBO_CLIENTE C
LEFT JOIN GER_TERCEIROS ON TER_CODIGO_ENTIDADE = REC_CODIGO_ENTIDADE
INNER JOIN COM_SERIE_DOCUMENTACAO ON SER_CODIGO = REC_CODIGO_SERIE
INNER JOIN COM_DOCUMENTO on DOC_CODIGO = SER_CODIGO_DOCUMENTO

WHERE C.REC_CODIGO_FILIAL=",FILIAL, " AND REC_REFERENCIA LIKE '%",REFERENCIA,"%' AND 
REC_NOME_CLIENTE LIKE '%",CLIENTE,"%'");
IF(SITUACAO > 0) THEN
SET v_CON_ = CONCAT(v_CON_," AND REC_STATUS=1");
END IF;

IF(EMISSAO_INI <>'' AND EMISSAO_TERM <>'') THEN
  SET v_CON_ = CONCAT(v_CON_," AND REC_DATA_EMISSAO BETWEEN '",EMISSAO_INI, "' AND '", EMISSAO_TERM, "'");
ELSE IF(EMISSAO_INI IS NOT NULL AND EMISSAO_TERM IS NULL) THEN
  SET v_CON_ = CONCAT(v_CON_," AND REC_DATA_EMISSAO >= ",EMISSAO_INI);
ELSE IF(EMISSAO_INI IS NULL AND EMISSAO_TERM IS NOT NULL) THEN
  SET v_CON_ = CONCAT(v_CON_," AND REC_DATA_EMISSAO <= ", EMISSAO_TERM);
END IF;
END IF;
END IF;

IF(TIPO!='' AND TIPO !='A') THEN
SET v_CON_ = CONCAT(v_CON_," AND TER_TIPO = '",TIPO,"' ");
END IF; 

IF(DOCUMENTO > 0) THEN
SET v_CON_=CONCAT(v_CON_," AND REC_CODIGO_DOCUMENTO = ", DOCUMENTO);
END IF;

IF(SERIE > 0) THEN
SET v_CON_=CONCAT(v_CON_," AND REC_CODIGO_SERIE = ", SERIE);
END IF;

IF(SITUACAO > 0) THEN
 SET v_CON_=CONCAT(v_CON_," AND REC_STATUS =1 ");
END IF;


IF(ENTIDADE > 0) THEN
SET v_CON_=CONCAT(v_CON_," AND REC_CODIGO_ENTIDADE = ", ENTIDADE); 
END IF;


SET v_CON_ = CONCAT(v_CON_," ORDER BY REC_REFERENCIA, REC_DATA_EMISSAO");
#SELECT v_CON_;
SET @stmt_str = v_CON_;
PREPARE stmt FROM @stmt_str;
EXECUTE stmt;
DEALLOCATE PREPARE stmt;

END
;;
