SELECT SER_CODIGO, SER_DESCRICAO, SER_NUMERACAO, SER_TERMINO, SER_CODIGO_DOCUMENTO, SER_CODIGO_FILIAL, 
SER_ENTIDADE, SER_ANO, DOC_DESCRICAO, IFNULL(SER_IS_ACTIVE,0) AS SER_IS_ACTIVE, SER_INICIO
