 SET vCONSULTA = CONCAT(vCONSULTA," AND (SER_ANO = ", ANO, " OR YEAR(SER_TERMINO) =",ANO,")");
END IF;
IF(FILIAL > 0) THEN
 SET vCONSULTA = CONCAT(vCONSULTA," AND SER_CODIGO_FILIAL = ", FILIAL);
END IF;

SET @stmt_str = vCONSULTA;
PREPARE stmt FROM @stmt_str;
EXECUTE stmt;
