BEGIN
	#Routine body goes here...
 
	IF(ARMAZEM > 0) THEN

  SELECT IFNULL(STOCK_QUANTIDADE, 0) AS STOCK
	FROM
		COM_STOCK S
	INNER JOIN GER_ARMAZEM Z ON Z.ARM_CODIGO = S.STOCK_CODIGO_ARMAZEM
	WHERE S.STOCK_CODIGO_ARTIGO = ARTIGO AND S.STOCK_CODIGO_ARMAZEM = ARMAZEM;

  ELSE
  SELECT
		IFNULL(SUM(S.STOCK_QUANTIDADE), 0) AS STOCK
	FROM
		COM_STOCK S
	INNER JOIN GER_ARMAZEM Z ON Z.ARM_CODIGO = S.STOCK_CODIGO_ARMAZEM
	WHERE S.STOCK_CODIGO_ARTIGO = ARTIGO;
  END IF; 
END
