delimiter ;;
CREATE PROCEDURE `stp_COM_STOCK_ADICIONAR`(IN `ARTIGO` int,IN `ARMAZEM` int,IN `QUANTIDADE` decimal(18,2), IN `CONTAGEM` decimal(18,2), IN `MAXIMA` decimal(18,2), IN `MINIMO` decimal(18,2))
BEGIN
	#Routine body goes here...
DECLARE PRECO_CUSTO_MEDIO DECIMAL(18,2) DEFAULT 0;

IF(NOT EXISTS(SELECT * FROM COM_STOCK WHERE STOCK_CODIGO_ARMAZEM=ARMAZEM AND STOCK_CODIGO_ARTIGO=ARTIGO))THEN

	INSERT INTO COM_STOCK (STOCK_CODIGO_ARMAZEM, STOCK_CODIGO_ARTIGO, STOCK_QUANTIDADE, STOCK_CONTAGEM,STOCK_QTD_MINIMA, STOCK_QTD_MAXIMA)
	VALUES(ARMAZEM, ARTIGO, QUANTIDADE, 0, MINIMO, MAXIMA);  
/*
ELSE
  UPDATE COM_STOCK SET
  STOCK_QTD_MINIMA = MINIMO, 
  STOCK_QTD_MAXIMA = MAXIMA,
	STOCK_QUANTIDADE = QUANTIDADE
 WHERE STOCK_CODIGO_ARMAZEM=ARMAZEM AND STOCK_CODIGO_ARTIGO=ARTIGO;*/
END IF;

SELECT ART_PCM_CALCULADO INTO PRECO_CUSTO_MEDIO FROM GER_ARTIGO WHERE ART_CODIGO = ARTIGO;
UPDATE COM_STOCK SET STOCK_CUSTO_MEDIO = PRECO_CUSTO_MEDIO WHERE STOCK_CODIGO_ARTIGO=ARTIGO;
END
;;
