BEGIN
	#Routine body goes here...
SELECT LOT_ID, LOT_REFERENCE, LOT_INCOME_UNIT, LOT_STOCK_UNIT, LOT_OUTCOME_UNIT, LOT_SALES_UNIT, LOT_PURCHAGE_UNIT,
SUM(MOV_STK_ART_QUANTIDADE) AS EXISTENCIA FROM STOCK_MOVIMENTOS_DIRECTOS_ARTIGOS 
INNER JOIN GER_ARTIGO ON ART_CODIGO = MOV_STK_ART_CODIGO_ARTIGO
INNER JOIN COM_STOCK ON STOCK_CODIGO_ARTIGO = ART_CODIGO
INNER JOIN GER_ARMAZEM ON ARM_CODIGO = STOCK_CODIGO_ARMAZEM
INNER JOIN GER_CATEGORIA ON CAT_CODIGO = ART_CATEGORIA 
