BEGIN
	#Routine body goes here...
SELECT S.STOCK_CODIGO_ARTIGO, S.STOCK_CODIGO_ARMAZEM, ARM_DESCRICAO, IFNULL(S.STOCK_QUANTIDADE,0) AS STOCK_QUANTIDADE, 
IFNULL(S.STOCK_CONTAGEM, 0) AS STOCK_CONTAGEM, IFNULL(S.STOCK_QTD_MINIMA, 0) AS STOCK_QTD_MINIMA, 
IFNULL(S.STOCK_QTD_MAXIMA,0) AS STOCK_QTD_MAXIMA, IFNULL(STOCK_QTD_ENC_CLIENTE, 0) AS STOCK_QTD_ENC_CLIENTE, 
IFNULL(STOCK_QTD_ENC_FORNECEDOR,0) AS STOCK_QTD_ENC_CLIENTE, STOCK_DATA_ULTIMA_ENTRADA,
STOCK_DATA_ULTIMA_SAIDA, STOCK_DATA_ULTIMA_CONTAGEM, IFNULL(STOCK_CUSTO_MEDIO,0) AS STOCK_CUSTO_MEDIO, 
IFNULL(ART_PRECO_VENDA,0) AS ART_PRECO_VENDA, IFNULL(ARM_POS, 0) AS ARM_POS  FROM COM_STOCK S 
INNER JOIN GER_ARMAZEM ON ARM_CODIGO = STOCK_CODIGO_ARMAZEM
INNER JOIN GER_ARTIGO ON ART_CODIGO = STOCK_CODIGO_ARTIGO
WHERE STOCK_CODIGO_ARTIGO = ARTIGO;
