-- ----------------------------
-- Procedure structure for stp_COM_STOCK_OBTERPORFILTRO
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_COM_STOCK_OBTERPORFILTRO`;
delimiter ;;
CREATE PROCEDURE `stp_COM_STOCK_OBTERPORFILTRO`(IN `WAREHOUSE_ID` int,IN `ARTIGO` varchar(120), IN `REFERENCIA` varchar(20), IN `CODIGO_BARRAS` varchar(120), IN `FILIAL` int, IN LOTE_ID INT, IN DIMENSAO_ID INT, SERIAL_ID INT, IN SEMELHANTE_ID INT, IN FAMILIA_ID INT, UTILIZADOR VARCHAR(20))
BEGIN
	#Routine body goes here...
DECLARE vCONSULTA LONGTEXT; 
DECLARE SUBFAMILIA INT DEFAULT NULL;
DECLARE PERFIL_ID INT DEFAULT 0;
DECLARE UTILIZADOR_ID INT DEFAULT 0;
SELECT UTI_CODIGO_PERFIL, UTI_CODIGO INTO PERFIL_ID, UTILIZADOR_ID FROM SIS_UTILIZADOR WHERE UTI_UTILIZADOR = UTILIZADOR; 

SET vCONSULTA = CONCAT("
SELECT
	A.ART_CODIGO,
	A.ART_REFERENCIA,
	A.ART_DESIGNACAO,
	CAT_DESCRICAO,
	STOCK_QUANTIDADE,
	A.ART_PRECO_CUSTO,
	A.ART_PRECO_VENDA,
	ARM_DESCRICAO,
	ART_IMAGEM,
	ART_CODIGO_BARRAS,
	IFNULL(LOT_CODIGO, - 1) AS LOT_CODIGO,
	LOT_REFERENCIA,
	IFNULL(DIM_CODIGO, - 1) AS DIM_CODIGO,
	DIM_REFERENCIA,
	UPPER(UNI_SIGLA) AS UNI_SIGLA,
	IFNULL(SERIAL_CODIGO, - 1) AS SERIAL_CODIGO,
	SERIAL_SERIAL_NUMBER
FROM
	GER_ARTIGO A
INNER JOIN COM_STOCK ON STOCK_CODIGO_ARTIGO = ART_CODIGO
INNER JOIN GER_ARMAZEM ON ARM_CODIGO = STOCK_CODIGO_ARMAZEM
INNER JOIN GER_CATEGORIA ON CAT_CODIGO = ART_CATEGORIA
INNER JOIN GER_UNIDADE_MEDICAO ON UNI_CODIGO = ART_UNIDADE_VENDA
LEFT JOIN GER_ARTIGO_LOTE ON LOT_CODIGO_ARTIGO = ART_CODIGO
LEFT JOIN GER_ARTIGO_DIMENSOES ON DIM_CODIGO_ARTIGO = ART_CODIGO
LEFT JOIN GER_ARTIGO_SERIAL_NUMBER ON SERIAL_ARTIGO_ID = ART_CODIGO
WHERE
	ART_STATUS != 'E' AND (ART_DESIGNACAO LIKE '%",ARTIGO,"%' OR ART_REFERENCIA LIKE '%",
REFERENCIA,"%' OR ART_CODIGO_BARRAS LIKE '%",CODIGO_BARRAS,"%')");

IF(FILIAL > 0 ) THEN
 SET vCONSULTA = CONCAT(vCONSULTA," AND ART_CODIGO_FILIAL = ",FILIAL);
END IF;
IF(WAREHOUSE_ID > 0 ) THEN
 SET vCONSULTA = CONCAT(vCONSULTA," AND STOCK_CODIGO_ARMAZEM = ",WAREHOUSE_ID);
ELSE
SET vCONSULTA = CONCAT(vCONSULTA," AND (STOCK_CODIGO_ARMAZEM IN (SELECT WAR_PERM_ARMAZEM_ID FROM GER_ARMAZEM_PERMISSOES
WHERE WAR_PERM_STATUS = 1 AND (WAR_PERM_PERFIL_ID =",PERFIL_ID," OR WAR_PERM_UTILIZADOR_ID = ",UTILIZADOR_ID,")))");
END IF;

IF(LOTE_ID > 0 ) THEN
 SET vCONSULTA = CONCAT(vCONSULTA," AND LOT_CODIGO = ",LOTE_ID);
END IF;

IF(DIMENSAO_ID > 0 ) THEN
 SET vCONSULTA = CONCAT(vCONSULTA," AND DIM_CODIGO = ",DIMENSAO_ID);
END IF;

IF(FAMILIA_ID > 0) THEN
  SELECT CAT_CODIGO INTO SUBFAMILIA FROM GER_CATEGORIA WHERE CAT_CATEGORIA = FAMILIA_ID; 
	IF(SUBFAMILIA IS NULL OR SUBFAMILIA<=0) THEN
    SET vCONSULTA = CONCAT(vCONSULTA," AND ART_CATEGORIA = ",FAMILIA_ID);
	ELSE
    SET vCONSULTA = CONCAT(vCONSULTA," AND (ART_CATEGORIA = ",FAMILIA_ID, " OR ART_CATEGORIA = ", SUBFAMILIA, ")");
	END IF;

END IF;

SET @stmt_str = vCONSULTA;
PREPARE stmt FROM @stmt_str;
EXECUTE stmt;
DEALLOCATE PREPARE stmt;

END
;;
delimiter ;

