-- ----------------------------
-- Procedure structure for stp_CONTB_PLANO_CONTA_OBTERPORFILTRO
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_CONTB_PLANO_CONTA_OBTERPORFILTRO`;
delimiter ;;
CREATE PROCEDURE `stp_CONTB_PLANO_CONTA_OBTERPORFILTRO`(IN NUMERO VARCHAR(10), IN DESCRICAO VARCHAR(50), IN CLASSE VARCHAR(1), IN NATUREZA VARCHAR(1))
BEGIN
	#Routine body goes here...
DECLARE vCONS LONGTEXT;

SET vCONS = CONCAT("
SELECT 
P.PLAN_CODIGO,
P.PLAN_CONTA,
P.PLAN_DESCRICAO,
IFNULL(P.PLAN_CODIGO_PAI, 0) AS PLAN_CODIGO_PAI,
P.PLAN_ESTADO,
P.PLAN_NATUREZA,
P.PLAN_CLASSE,
IFNULL(P.PLAN_ANO_EXERCICIO, YEAR(CURDATE())) AS PLAN_ANO_EXERCICIO,
CONCAT(CP.PLAN_CONTA,'-',CP.PLAN_DESCRICAO) AS RUBRICA
FROM CONTB_PLANO_CONTAS P 
LEFT JOIN CONTB_PLANO_CONTAS CP ON CP.PLAN_CODIGO = P.PLAN_CODIGO_PAI
WHERE P.PLAN_CONTA LIKE '%",NUMERO,"%' AND P.PLAN_DESCRICAO LIKE '%",DESCRICAO,"%' " ); 
 IF(NATUREZA !='' AND NATUREZA IS NOT NULL) THEN
  SET vCONS = CONCAT(vCONS," AND P.PLAN_NATUREZA ='",NATUREZA,"'");
 END IF;
 IF(CLASSE !='' AND CLASSE IS NOT NULL) THEN
  SET vCONS = CONCAT(vCONS," AND P.PLAN_CLASSE = '",CLASSE,"'");
 END IF;
 
SET vCONS = CONCAT(vCONS," ORDER BY P.PLAN_CONTA, P.PLAN_DESCRICAO ");


SET @stmt_str = vCONS;
PREPARE stmt FROM @stmt_str;
EXECUTE stmt;
DEALLOCATE PREPARE stmt;

END
;;
delimiter ;

