-- ----------------------------
-- Procedure structure for stp_FIN_ARTIGOS_MAIS_VENDIDO
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_FIN_ARTIGOS_MAIS_VENDIDO`;
delimiter ;;
CREATE PROCEDURE `stp_FIN_ARTIGOS_MAIS_VENDIDO`(IN FILIAL INT, ANO INT, WAREHOUSE_ID INT, UTILIZADOR VARCHAR(20))
BEGIN
	#Routine body goes here...
DECLARE PERFIL_ID INT DEFAULT 0;
DECLARE UTILIZADOR_ID INT DEFAULT 0; 
DECLARE v_CON_ LONGTEXT;

SELECT UTI_CODIGO_PERFIL, UTI_CODIGO INTO PERFIL_ID, UTILIZADOR_ID FROM SIS_UTILIZADOR WHERE UTI_UTILIZADOR = UTILIZADOR;
SET v_CON_ = CONCAT(" 
SELECT ART_CODIGO,  CONCAT(ART_DESIGNACAO,' (',ART_REFERENCIA,')') AS ART_DESIGNACAO,  SUM(FAT_ITEM_QUANTIDADE) AS FAT_ITEM_QUANTIDADE, F.FAT_ITEM_PRECO AS SALES_PRICE, 
(SUM(FAT_ITEM_QUANTIDADE)  * SUM(F.FAT_ITEM_PRECO))TOTAL_SALES, ART_IMAGEM, ART_MOVIMENTA_STOCK, ART_PEDIDO_COZINHA, ART_RESTPOS

FROM COM_FATURA_CLIENTE_ITEM F
INNER JOIN GER_ARTIGO A ON A.ART_CODIGO=F.FAT_ITEM_CODIGO_ARTIGO
INNER JOIN COM_FATURA_CLIENTE D ON D.FAT_CODIGO = F.FAT_ITEM_CODIGO_FATURA
INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = FAT_CODIGO_DOCUMENTO
WHERE D.FAT_STATUS = 1 AND FAT_STATUS_DOCUMENTO = 8 AND (FAT_ITEM_STATUS IS NULL OR FAT_ITEM_STATUS=1) AND D.FAT_CODIGO_FILIAL=",FILIAL," AND A.ART_STATUS='A'  AND FAT_NUMERACAO > 0
AND DOC_FORMATO IN ('INVOICE_R', 'INVOICE') AND FAT_CODIGO_SERIE 
IN (SELECT SER_CODIGO FROM COM_SERIE_DOCUMENTACAO WHERE (SER_ANO = ", ANO, " OR YEAR(SER_TERMINO) =",ANO,"))
");

IF(WAREHOUSE_ID > 0) THEN
 SET v_CON_=CONCAT(v_CON_, " AND FAT_ITEM_WAREHOUSE_ID = ", WAREHOUSE_ID);
ELSE
 SET v_CON_=CONCAT(v_CON_," AND F.FAT_ITEM_WAREHOUSE_ID IN (SELECT WAR_PERM_ARMAZEM_ID FROM GER_ARMAZEM_PERMISSOES
WHERE WAR_PERM_STATUS = 1 AND (WAR_PERM_PERFIL_ID =",PERFIL_ID," OR WAR_PERM_UTILIZADOR_ID = ",UTILIZADOR_ID,"))");
END IF;

SET v_CON_ =CONCAT(v_CON_," GROUP BY ART_CODIGO, FAT_ITEM_PRECO ORDER BY ART_DESIGNACAO ");

#SELECT v_CON_;
SET @stmt_str = v_CON_;
PREPARE stmt FROM @stmt_str;
EXECUTE stmt;
DEALLOCATE PREPARE stmt;

END
;;
delimiter ;

