delimiter ;;
CREATE PROCEDURE `stp_FIN_BANCO_OBTERPORFILTRO`(IN `DESCRICAO` VARCHAR(50), IN `FILIAL` INT)
BEGIN
	#Routine body goes here...
DECLARE v_CON_ LONGTEXT;
DECLARE ENTIDADE VARCHAR(120);

SELECT ENT_NOME_COMPLETO INTO ENTIDADE FROM GER_ENTIDADE
INNER JOIN SIS_EMPRESA ON FIL_CODIGO = ENT_CODIGO
WHERE FIL_CODIGO_EMPRESA IS NULL;

SET v_CON_ = CONCAT("
SELECT B.BANC_CODIGO, BANC_SIGLA, CASE 
WHEN E.ENT_NOME_COMPLETO ='",ENTIDADE,"' THEN 'ENTIDADE FINANCEIRA LOCAL' 
ELSE E.ENT_NOME_COMPLETO END AS EMP_RAZAO_SOCIAL, BANC_CODIGO_FILIAL

FROM FIN_BANCO B
INNER JOIN GER_ENTIDADE E ON E.ENT_CODIGO = BANC_CODIGO
WHERE (ENT_NOME_COMPLETO LIKE '%",DESCRICAO,"%' OR
BANC_SIGLA LIKE'%",DESCRICAO,"%')
AND (BANC_CODIGO_FILIAL IS NULL OR BANC_CODIGO_FILIAL = ",FILIAL,") ORDER BY BANC_SIGLA");

SET @stmt_str = v_CON_;
PREPARE stmt FROM @stmt_str;
EXECUTE stmt;
DEALLOCATE PREPARE stmt;
END
;;
