delimiter ;;
CREATE PROCEDURE `stp_FIN_CAMBIO_ADICIONAR`(MOEDA int, CAMBIO decimal (18,5),
 INICIO date, TERMINO date, IN FILIAL INT)
BEGIN

DECLARE CODIGO INT;

IF( NOT EXISTS(SELECT * FROM FIN_CAMBIO WHERE CAM_CODIGO_MOEDA = MOEDA AND CAM_CAMBIO = CAMBIO AND CAM_FILIAL = FILIAL 
AND CAM_INICIO = INICIO)) THEN

SELECT CAM_CODIGO INTO CODIGO FROM FIN_CAMBIO WHERE CAM_CODIGO_MOEDA = MOEDA  AND CAM_TERMINO IS NULL AND CAM_FILIAL = FILIAL LIMIT 1;

IF(CODIGO > 0 OR CODIGO IS NOT NULL) THEN

UPDATE FIN_CAMBIO
SET CAM_TERMINO = CURDATE()
WHERE  CAM_CODIGO_MOEDA = MOEDA AND
CAM_TERMINO IS NULL AND CAM_FILIAL = FILIAL;

INSERT INTO FIN_CAMBIO
( 
		CAM_CODIGO_MOEDA,
		CAM_CAMBIO,
		CAM_INICIO,
		CAM_TERMINO, 
    CAM_FILIAL 
)
VALUES
(
    MOEDA,
		CAMBIO,
		INICIO,
		NULL, 
    FILIAL 
);
ELSE
INSERT INTO FIN_CAMBIO
( 	
	CAM_CODIGO_MOEDA,
	CAM_CAMBIO,
	CAM_INICIO,
	CAM_TERMINO,
  CAM_FILIAL 
)
VALUES
(
		
		MOEDA,
		CAMBIO,
		INICIO,
		NULL, 
    FILIAL 
);
 
END IF;
ELSE

UPDATE FIN_CAMBIO
SET CAM_TERMINO = CURDATE()
WHERE  CAM_CODIGO_MOEDA = MOEDA AND
CAM_TERMINO IS NULL AND CAM_FILIAL = FILIAL;

UPDATE FIN_CAMBIO
SET CAM_TERMINO = NULL
WHERE  CAM_CODIGO_MOEDA = MOEDA AND 
CAM_FILIAL = FILIAL AND CAM_CAMBIO = CAMBIO;



END IF;

 
END
;;
