CREATE PROCEDURE `stp_FIN_CONTA_BANCARIA_OBTERPORFILTRO`(IN NUMERO VARCHAR(20),
IN BANCO INT,
 IN MOEDA INT, IN SIGLA VARCHAR(10), IN FILIAL INT)
BEGIN
DECLARE v_CON_ LONGTEXT;

/*CALL stp_fin_CONTA_BANCARIA_UPDATE_SALDO(FILIAL);*/

SET v_CON_ =CONCAT("SELECT  
		  CONT_NUMERO,
		 CONT_CODIGO_BANCO,
		 CONT_CODIGO_MOEDA,
		 CONT_STATUS,
		 CONT_IBAN,
		 CONT_NIB,
		 CONT_SALDO,
     CONCAT(BANC_SIGLA,' - ',ENT_NOME_COMPLETO) AS BANC_SIGLA, 
     MOE_SIGLA,
     MOE_DESCRICAO,
     CONT_CORRENTE,
     CONT_DESCRICAO,
     CONT_TIPO,
     IFNULL(CONT_ENTITY_ID,-1) AS CONT_ENTITY_ID,
		 CONT_ID,
     IFNULL(CONT_PLANO_CONTAS, -1) AS CONT_PLANO_CONTAS,
     CONT_FILIAL, CONCAT(BANC_SIGLA,' - ', CONT_NUMERO,'(', MOE_SIGLA,')') AS DESCRICAO,
		 CONT_BENEFICIARIO
FROM  FIN_CONTA_BANCARIA
INNER JOIN FIN_BANCO B ON B.BANC_CODIGO =  CONT_CODIGO_BANCO
INNER JOIN FIN_MOEDA M ON MOE_CODIGO = CONT_CODIGO_MOEDA
INNER JOIN GER_ENTIDADE ON ENT_CODIGO = BANC_CODIGO
WHERE /*CONT_CORRENTE='S' AND*/ CONT_NUMERO LIKE '%", NUMERO,"%' AND BANC_SIGLA LIKE '%",SIGLA,"%' ");

IF(BANCO > 0)
THEN
SET v_CON_ = CONCAT(v_CON_ ,' AND  CONT_CODIGO_BANCO =', BANCO);
END IF;

IF(MOEDA > 0)
THEN
SET v_CON_ = CONCAT(v_CON_ ,' AND  CONT_CODIGO_MOEDA =', MOEDA);
END IF;

IF(FILIAL > 0) THEN
SET v_CON_ = CONCAT(v_CON_,' AND CONT_FILIAL=',FILIAL);
END IF;

SET @stmt_str = v_CON_;
PREPARE stmt FROM @stmt_str;
EXECUTE stmt;
DEALLOCATE PREPARE stmt;
END
;;
delimiter ;

