-- Procedure structure for stp_FIN_CONTA_BANCARIA_SALDO_PERIODO
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_FIN_CONTA_BANCARIA_SALDO_PERIODO`;
delimiter ;;
CREATE PROCEDURE `stp_FIN_CONTA_BANCARIA_SALDO_PERIODO`(IN  `INICIO` varchar(10),IN `TERMINO` varchar(10),IN `FILIAL` int)
BEGIN
	#Routine body goes here...
DECLARE done INT DEFAULT FALSE;
DECLARE BANCO VARCHAR(120);
DECLARE CONTA VARCHAR(50);
DECLARE v_ENTRADAS DECIMAL(18,0) DEFAULT 0;
DECLARE v_SAIDAS DECIMAL(18,0) DEFAULT 0;
DECLARE SALDO DECIMAL(18,0) DEFAULT 0;
 
DECLARE UNIDADE_FINANCEIRAS CURSOR FOR SELECT CONCAT(BANC_SIGLA,' - ',UPPER(EMP_RAZAO_SOCIAL)) AS ENTIDADE, C.CONT_NUMERO 
FROM FIN_CONTA_BANCARIA C
INNER JOIN FIN_BANCO ON CONT_CODIGO_BANCO = BANC_CODIGO
INNER JOIN GER_ENTIDADE_EMPRESA ON EMP_CODIGO = BANC_CODIGO
WHERE CONT_FILIAL =  FILIAL;

DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE;
DELETE FROM TMP_SALDO_PERIODO;
OPEN UNIDADE_FINANCEIRAS;
read_loop: LOOP

FETCH UNIDADE_FINANCEIRAS INTO BANCO, CONTA;
IF done THEN
      LEAVE read_loop;
 END IF;

SET v_ENTRADAS= (SELECT SUM(E.ENT_VALOR) FROM FIN_ENTRADA_CAIXA E 
INNER JOIN FIN_MOVIMENTO_CAIXA M ON E.ENT_CODIGO=M.MOV_CODIGO
INNER JOIN FIN_MOVIMENTO_CONTA_BANCARIA  ON CODIGO_MOVIMENTO = MOV_CODIGO  
WHERE   MOV_TIPO='E' AND MOV_FILIAL= FILIAL AND CONTA_BANCARIA = CONTA  
AND MOV_DATA >=STR_TO_DATE(INICIO,'%d-%m-%Y') AND MOV_DATA <= STR_TO_DATE(TERMINO,'%d-%m-%Y')
);

SET v_SAIDAS = (SELECT SUM(S.SAI_VALOR) FROM FIN_SAIDA_CAIXA S 
INNER JOIN FIN_MOVIMENTO_CAIXA M ON S.SAI_CODIGO=M.MOV_CODIGO
INNER JOIN FIN_MOVIMENTO_CONTA_BANCARIA  ON CODIGO_MOVIMENTO = MOV_CODIGO 
WHERE  MOV_TIPO='S' AND MOV_FILIAL=FILIAL AND CONTA_BANCARIA =CONTA 
AND MOV_DATA >=  STR_TO_DATE(INICIO,'%d-%m-%Y') AND MOV_DATA <= STR_TO_DATE(TERMINO,'%d-%m-%Y') 
);
 
IF(v_ENTRADAS IS NULL) THEN
SET v_ENTRADAS =0;
END IF;

IF(v_SAIDAS IS NULL) THEN
SET v_SAIDAS=0;
END IF;

SET SALDO = (fn_FIN_SALDO_ANTERIOR(INICIO, FILIAL, CONTA) + v_ENTRADAS) - v_SAIDAS;
INSERT INTO TMP_SALDO_PERIODO VALUES(BANCO, CONTA, fn_FIN_SALDO_ANTERIOR(INICIO, FILIAL, CONTA), v_ENTRADAS, v_SAIDAS, SALDO);

END LOOP read_loop;
CLOSE UNIDADE_FINANCEIRAS;


SELECT S.* FROM TMP_SALDO_PERIODO S;
END
;;
delimiter ;

