DROP PROCEDURE IF EXISTS `stp_fin_CONTA_BANCARIA_UPDATE_SALDO`;
delimiter ;;
CREATE PROCEDURE `stp_fin_CONTA_BANCARIA_UPDATE_SALDO`(IN `FILIAL` int)
BEGIN
	#Routine body goes here...
DECLARE FINISHED INT DEFAULT 0;

DECLARE CONTA VARCHAR(20);
DECLARE SALDO DECIMAL(18,0) DEFAULT 0;

DECLARE CURSOR_CONTA CURSOR FOR
 SELECT CONT_NUMERO FROM FIN_CONTA_BANCARIA WHERE CONT_FILIAL = FILIAL;

DECLARE CONTINUE HANDLER
FOR NOT FOUND SET finished = 1;

OPEN CURSOR_CONTA;

obterConta: LOOP

FETCH CURSOR_CONTA INTO CONTA;

IF(FINISHED =1) THEN
 LEAVE obterConta;
end if;

SELECT SUM(MC.MOV_CONT_VALOR) INTO SALDO FROM FIN_MOVIMENTO_CONTA_BANCARIA MC WHERE 
MC.CONTA_BANCARIA = CONTA;

IF(SALDO IS NULL) THEN
 SET SALDO = 0;
END IF;

UPDATE FIN_CONTA_BANCARIA SET
CONT_SALDO = SALDO
WHERE CONT_NUMERO = CONTA;

END LOOP obterConta;

CLOSE CURSOR_CONTA;

END
;;
