delimiter ;;
CREATE PROCEDURE `stp_FIN_CONTA_PAGAR_RECEBER_IMPORTAR_PENDENTES`(IN `FILIAL` int)
BEGIN
	#Routine body goes here...
declare totalCustomerInvoices int DEFAULT 0;
declare totalSupplierInvoices int DEFAULT 0;


SELECT COUNT(FAT_CODIGO) INTO totalCustomerInvoices  FROM COM_FATURA_CLIENTE
INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = FAT_CODIGO_DOCUMENTO
WHERE FAT_CODIGO_FILIAL = FILIAL AND FAT_NUMERACAO > 0 AND  FAT_STATUS =1 AND DOC_FORMATO="INVOICE"
AND (((FAT_VALOR_TOTAL - IFNULL(FAT_VALOR_RETENCAO, 0)) - FAT_VALOR_PAGO) > 0 OR (FAT_VALOR_TOTAL - FAT_VALOR_PAGO) < 0) AND FAT_REFERENCIA NOT IN(SELECT CONTA_REFERENCIA FROM FIN_CONTA_PAGAR_RECEBER WHERE CONTA_ENTIDADE = FAT_CODIGO_CLIENTE);

IF(totalCustomerInvoices > 0) THEN
	INSERT INTO FIN_CONTA_PAGAR_RECEBER(CONTA_FILIAL_ID,
							CONTA_ENTIDADE,
							CONTA_DOCUMENT_ID,
							CONTA_REFERENCIA,
							CONTA_EMISSAO,
							CONTA_VENCIMENTO,
							CONTA_PARCELA_ID,
							CONTA_STATUS,
							CONTA_TITULO,
							CONTA_DESCRICAO,
							CONTA_NATUREZA,
							CONTA_RUBRICA_ID,
							CONTA_VALOR,
							CONTA_PENDENTE,
							CONTA_CREATED_BY,
							CONTA_CREATED_DATE,
							CONTA_UPDATED_BY,
							CONTA_UPDATED_DATE,
							CONTA_MOEDA_ID,
							CONTA_CAMBIO,
							CONTA_VALOR_INCIDENCIA,
							CONTA_VALOR_DESCONTO,
							CONTA_IVA,
							CONTA_RETENCAO)
							SELECT FAT_CODIGO_FILIAL, FAT_CODIGO_CLIENTE, FAT_CODIGO_DOCUMENTO, FAT_REFERENCIA, FAT_DATA_EMISSAO, FAT_DATA_VALIDADE, 1, 1, CONCAT(DOC_DESCRICAO, " ", FAT_REFERENCIA), "", "R", NULL, FAT_VALOR_TOTAL - FAT_VALOR_RETENCAO, FAT_VALOR_TOTAL - FAT_VALOR_RETENCAO,FAT_CREATED_BY, FAT_CREATED_DATE, FAT_UPDATE_BY, FAT_UPDATED_DATE, FAT_CODIGO_MOEDA, FAT_CAMBIO, FAT_TOTAL_LIQUIDO, FAT_TOTAL_DESCONTOS, FAT_TOTAL_IMPOSTOS, FAT_VALOR_RETENCAO  FROM COM_FATURA_CLIENTE
					INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = FAT_CODIGO_DOCUMENTO
					 WHERE FAT_CODIGO_FILIAL = FILIAL AND FAT_NUMERACAO > 0 AND  FAT_STATUS =1 AND DOC_FORMATO="INVOICE"
	AND (((FAT_VALOR_TOTAL - IFNULL(FAT_VALOR_RETENCAO, 0)) - FAT_VALOR_PAGO) > 0 OR (FAT_VALOR_TOTAL - FAT_VALOR_PAGO) < 0) AND FAT_REFERENCIA NOT IN(SELECT CONTA_REFERENCIA FROM FIN_CONTA_PAGAR_RECEBER WHERE CONTA_ENTIDADE = FAT_CODIGO_CLIENTE);
END IF;	
	

SELECT COUNT(FAT_CODIGO) INTO totalSupplierInvoices FROM COM_FATURA_FORNECEDOR
				INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = FAT_CODIGO_DOCUMENTO
				 WHERE FAT_CODIGO_FILIAL = FILIAL AND FAT_NUMERACAO > 0 AND  FAT_STATUS =1 AND  DOC_FORMATO="SUPPLIER_I" 
				 AND (((FAT_VALOR_TOTAL - IFNULL(FAT_VALOR_RETENCAO, 0)) - FAT_VALOR_PAGO) > 0 OR (FAT_VALOR_TOTAL - FAT_VALOR_PAGO) < 0)
				 AND FAT_REFERENCIA NOT IN(SELECT CONTA_REFERENCIA FROM FIN_CONTA_PAGAR_RECEBER WHERE CONTA_ENTIDADE = FAT_CODIGO_FORNECEDOR);

IF(totalSupplierInvoices > 0) THEN
INSERT INTO FIN_CONTA_PAGAR_RECEBER(CONTA_FILIAL_ID,
						CONTA_ENTIDADE,
						CONTA_DOCUMENT_ID,
						CONTA_REFERENCIA,
						CONTA_EMISSAO,
						CONTA_VENCIMENTO,
						CONTA_PARCELA_ID,
						CONTA_STATUS,
						CONTA_TITULO,
						CONTA_DESCRICAO,
						CONTA_NATUREZA,
						CONTA_RUBRICA_ID,
						CONTA_VALOR,
						CONTA_PENDENTE,
						CONTA_CREATED_BY,
						CONTA_CREATED_DATE,
						CONTA_UPDATED_BY,
						CONTA_UPDATED_DATE,
						CONTA_MOEDA_ID,
						CONTA_CAMBIO,
						CONTA_VALOR_INCIDENCIA,
						CONTA_VALOR_DESCONTO,
						CONTA_IVA,
						CONTA_RETENCAO)
           SELECT FAT_CODIGO_FILIAL, FAT_CODIGO_FORNECEDOR, FAT_CODIGO_DOCUMENTO, FAT_REFERENCIA, FAT_DATA_EMISSAO, FAT_DATA_VALIDADE, 1, 1, CONCAT(DOC_DESCRICAO, " ", FAT_REFERENCIA), "", "P", NULL, FAT_VALOR_TOTAL - FAT_VALOR_RETENCAO, FAT_VALOR_TOTAL - FAT_VALOR_RETENCAO,FAT_CREATED_BY, FAT_CREATED_DATE, FAT_UPDATE_BY, FAT_UPDATED_DATE, FAT_CODIGO_MOEDA, FAT_CAMBIO, FAT_TOTAL_LIQUIDO, FAT_TOTAL_DESCONTOS, FAT_TOTAL_IMPOSTOS, FAT_VALOR_RETENCAO  FROM COM_FATURA_FORNECEDOR
				INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = FAT_CODIGO_DOCUMENTO
				 WHERE FAT_CODIGO_FILIAL = FILIAL AND FAT_NUMERACAO > 0 AND  FAT_STATUS =1 AND  DOC_FORMATO="SUPPLIER_I" 
				 AND (((FAT_VALOR_TOTAL - IFNULL(FAT_VALOR_RETENCAO, 0)) - FAT_VALOR_PAGO) > 0 OR (FAT_VALOR_TOTAL - FAT_VALOR_PAGO) < 0)
				 AND FAT_REFERENCIA NOT IN(SELECT CONTA_REFERENCIA FROM FIN_CONTA_PAGAR_RECEBER WHERE CONTA_ENTIDADE = FAT_CODIGO_FORNECEDOR);
END IF;

SELECT totalCustomerInvoices,  totalSupplierInvoices;
				  
	
END
;;
