-- ----------------------------
-- Procedure structure for stp_FIN_CONTA_PAGAR_RECEBER_OBTERPORFILTRO
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_FIN_CONTA_PAGAR_RECEBER_OBTERPORFILTRO`;
delimiter ;;
CREATE PROCEDURE `stp_FIN_CONTA_PAGAR_RECEBER_OBTERPORFILTRO`(IN `CODIGO` int,IN `DOCUMENTO` int,IN `REFERENCIA` varchar(50),IN `ENTIDADE` int,IN `RUBRICA` int,IN `EMISSAO_INI` date,IN `EMISSAO_TERM` date,IN `VENCIMENTO_INI` date,IN `VENCIMENTO_TERM` date, IN FILIAL INT)
BEGIN
	#Routine body goes here...
DECLARE v_CONSULTA LONGTEXT;


SET v_CONSULTA =CONCAT(" 
 SELECT C.*, ENT_NOME_COMPLETO, CONCAT(RUB_CODIGO, ' - ', RUB_DESCRICAO) AS RUBRICA, DOC_DESCRICAO, MOE_SIGLA FROM   FIN_CONTA_PAGAR_RECEBER C
	INNER JOIN GER_ENTIDADE ON ENT_CODIGO = CONTA_ENTIDADE 
	LEFT JOIN FIN_CONTAS_TESOURARIA R ON RUB_CODIGO = CONTA_RUBRICA_ID 
	LEFT JOIN COM_DOCUMENTO ON DOC_CODIGO = CONTA_DOCUMENT_ID
  INNER JOIN FIN_MOEDA ON MOE_CODIGO = CONTA_MOEDA_ID
");
	
	IF (CODIGO > 0) THEN
		
		SET v_CONSULTA=CONCAT(v_CONSULTA," WHERE CONTA_CODIGO = ", CODIGO);
	ELSE
	  SET v_CONSULTA=CONCAT(v_CONSULTA," WHERE 1 = 1 ");
		
		IF(FILIAL >0) THEN
      SET v_CONSULTA=CONCAT(v_CONSULTA," AND CONTA_FILIAL_ID=", FILIAL);
    END IF; 

     IF(DOCUMENTO > 0) THEN
		   SET v_CONSULTA=CONCAT(v_CONSULTA," WHERE CONTA_DOCUMENT_ID = ", DOCUMENTO); 
     END 	IF;
		 
		  IF(ENTIDADE > 0) THEN
		   SET v_CONSULTA=CONCAT(v_CONSULTA," WHERE CONTA_ENTIDADE = ", ENTIDADE); 
     END 	IF;
		 
		 IF(RUBRICA > 0) THEN
		   SET v_CONSULTA=CONCAT(v_CONSULTA," WHERE CONTA_RUBRICA_ID = ", ENTIDADE); 
     END 	IF;
		 
		 IF(EMISSAO_INI IS NOT NULL AND EMISSAO_TERM IS NOT NULL) 	THEN
		 SET v_CONSULTA = CONCAT(v_CONSULTA ," AND CONTA_EMISSAO BETWEEN  '",EMISSAO_INI, "' AND '", EMISSAO_TERM, "'");
		ELSE
		IF(EMISSAO_INI IS NOT NULL AND EMISSAO_TERM IS  NULL) 	THEN
		 SET v_CONSULTA = CONCAT(v_CONSULTA ," AND CONTA_EMISSAO >= '",EMISSAO_INI, "'");
		ELSE
		IF(EMISSAO_INI IS NULL AND EMISSAO_TERM IS NOT NULL ) 	THEN
		 SET v_CONSULTA = CONCAT(v_CONSULTA ," AND CONTA_EMISSAO <= '",EMISSAO_TERM, "'");
		END IF;
		END IF;
		END IF;
		
		
		IF(VENCIMENTO_INI IS NOT NULL AND VENCIMENTO_TERM IS NOT NULL) 	THEN
		 SET v_CONSULTA = CONCAT(v_CONSULTA ," AND CONTA_VENCIMENTO BETWEEN  '",VENCIMENTO_INI, "' AND '", VENCIMENTO_TERM, "'");
		ELSE
		IF(VENCIMENTO_INI IS NOT NULL AND VENCIMENTO_TERM IS  NULL) 	THEN
		 SET v_CONSULTA = CONCAT(v_CONSULTA ," AND CONTA_VENCIMENTO >= '",VENCIMENTO_INI, "'");
		ELSE
		IF(VENCIMENTO_INI IS NULL AND VENCIMENTO_TERM IS NOT NULL ) 	THEN
		 SET v_CONSULTA = CONCAT(v_CONSULTA ," AND CONTA_VENCIMENTO <= '",VENCIMENTO_TERM, "'");
		END IF;
		END IF;
		END IF;
	
	END IF;
  

 SET @stmt_str = v_CONSULTA;
 PREPARE stmt FROM @stmt_str;
 EXECUTE stmt;
 DEALLOCATE PREPARE stmt;
END
;;
delimiter ;

