delimiter ;;
CREATE PROCEDURE `stp_FIN_MOVIMENTO_ADICIONAR`(IN `DATA_MOVIMENTO` date,IN `DATA_TRANSACAO` datetime,
IN `MOEDA` int,IN `MOVIMENTO` varchar(1),IN `PAGAMENTO` int,IN `UTILIZADOR` varchar(20),IN `DESCRITIVO` varchar(120),
IN `FLUXO_CAIXA` int,IN `CONTA` varchar(20),IN `ENTIDADE` int,IN `VALOR` decimal(18,2),IN `OBS` varchar(255), 
IN FATURA INT, IN POS INT, FILIAL INT, DOC_PAGTO VARCHAR(20), DOCUMENT_TYPE INT , DOCUMENT_ID INT, SERIE_ID INT,
REALIZADO INT, MOVIMENTO_PERIODICO_ID INT, ACCOUNT_ORIGEM_ID VARCHAR(20), ACCOUNT_DESTINO_ID VARCHAR(20))
BEGIN
	#Routine body goes here...
DECLARE CODIGO INT DEFAULT 0;

IF(DOCUMENT_TYPE<=0 OR DOCUMENT_ID <=0) THEN
	SET DOCUMENT_TYPE = NULL;
	SET DOCUMENT_ID = NULL; 
END IF;

IF(CONTA IS NULL OR CONTA <=0) THEN
 SET CONTA ="CAIXA";
 SET DOC_PAGTO ="NUMERÁRIO";
END IF;

IF(MOEDA <=0) THEN

SELECT CONT_CODIGO_MOEDA INTO MOEDA FROM fin_conta_bancaria WHERE CONT_NUMERO = CONTA
AND CONT_FILIAL = FILIAL;

END IF;

IF(FLUXO_CAIXA <=0) THEN
 SET FLUXO_CAIXA = NULL;
END IF;

IF(SERIE_ID <=0) THEN
SET SERIE_ID = NULL;
END IF;

IF(FATURA > 0) THEN 
 SET REALIZADO = 1; 
END IF;

IF(ENTIDADE <=0) THEN
 SET ENTIDADE = NULL;
END IF;

	INSERT INTO FIN_MOVIMENTO_CAIXA (MOV_CONTA_BANCARIA, MOV_DATA, MOV_DATA_LANCAMANTO, MOV_CODIGO_MOEDA, MOV_TRANSACAO, 
	MOV_MEIO_PAGAMENTO, MOV_UTILIZADOR, MOV_CODIGO_FILIAL, MOV_DESCRICAO, MOV_FLUXO_CAIXA, MOV_VALOR, MOV_ENTIDADE, 
	MOV_OBSERVACOES, MOV_STATUS, MOV_NUM_DOC_PAGTO, MOV_DOCUMENT_TYPE, MOV_DOCUMENT_ID, MOV_TREASURE_SERIE_ID, MOV_REAL) 
  VALUES (CONTA, DATA_TRANSACAO, DATA_MOVIMENTO, MOEDA, MOVIMENTO, PAGAMENTO, UTILIZADOR, FILIAL, 
	DESCRITIVO, FLUXO_CAIXA, VALOR, ENTIDADE, OBS,1, DOC_PAGTO, DOCUMENT_TYPE, DOCUMENT_ID, SERIE_ID, REALIZADO);
	SELECT LAST_INSERT_ID() INTO CODIGO;
	IF(FATURA > 0) THEN
		INSERT INTO FIN_MOVIMENTO_FATURA VALUES (CODIGO, FATURA, 1);
	END IF;
	IF(POS > 0) THEN
		INSERT INTO COM_POS_MOVIMENTO VALUES (POS, CODIGO, 1);                                                                                                                                                                                                               
	END IF;
END
;;
delimiter ;

