-- Procedure structure for stp_FIN_MOVIMENTO_CUSTOMER_RECEIPTS_DAILY
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_FIN_MOVIMENTO_CUSTOMER_RECEIPTS_DAILY`;
delimiter ;;
CREATE PROCEDURE `stp_FIN_MOVIMENTO_CUSTOMER_RECEIPTS_DAILY`(IN `PERIODO_INICIO` DATE, IN `PERIODO_FIM` DATE, IN FILTER VARCHAR(2), IN FILIAL INT, IN ENTITY_ID INT, ACCOUNT_ID VARCHAR(20))
BEGIN
	#Routine body goes here...
IF(FILTER="L") THEN

SELECT DISTINCT DATE(MOV_DATA_LANCAMANTO) AS DATA_TRANSACAO, SUM(REC_VALOR_TOTAL) AS VALOR FROM FIN_MOVIMENTO_CAIXA 
INNER JOIN COM_RECIBO_CLIENTE ON REC_CODIGO = MOV_DOCUMENT_ID  
INNER JOIN COM_DOCUMENTO L ON L.DOC_CODIGO = REC_CODIGO_DOCUMENTO  
WHERE MOV_TRANSACAO='E' AND DATE(MOV_DATA_LANCAMANTO)  BETWEEN PERIODO_INICIO AND PERIODO_FIM
AND REC_STATUS = 1 AND MOV_CODIGO_FILIAL = FILIAL AND MOV_STATUS = 1 AND MOV_DOCUMENT_TYPE = REC_CODIGO_DOCUMENTO
GROUP BY MOV_DATA_LANCAMANTO
UNION
SELECT DISTINCT DATE(MOV_DATA_LANCAMANTO) AS DATA_TRANSACAO, SUM(MOV_VALOR- FAT_TROCO) AS VALOR FROM FIN_MOVIMENTO_CAIXA  
INNER JOIN FIN_MOVIMENTO_FATURA ON MOV_CODIGO_MOVIMENTO = MOV_CODIGO
INNER JOIN COM_FATURA_CLIENTE ON FAT_CODIGO = MOV_CODIGO_FATURA 
INNER JOIN COM_DOCUMENTO F ON F.DOC_CODIGO = FAT_CODIGO_DOCUMENTO
WHERE MOV_TRANSACAO='E' AND DATE(MOV_DATA_LANCAMANTO)  BETWEEN PERIODO_INICIO AND PERIODO_FIM
AND FAT_STATUS = 1 AND MOV_CODIGO_FILIAL = FILIAL AND MOV_STATUS = 1
GROUP BY MOV_DATA_LANCAMANTO;
ELSE
SELECT DISTINCT DATE(MOV_DATA_LANCAMANTO) AS DATA_TRANSACAO, SUM(REC_VALOR_TOTAL) AS VALOR FROM FIN_MOVIMENTO_CAIXA 
INNER JOIN COM_RECIBO_CLIENTE ON REC_CODIGO = MOV_DOCUMENT_ID 
INNER JOIN COM_DOCUMENTO L ON L.DOC_CODIGO = REC_CODIGO_DOCUMENTO  
WHERE MOV_TRANSACAO='E' AND  MOV_DATA BETWEEN PERIODO_INICIO AND PERIODO_FIM
AND REC_STATUS = 1 AND MOV_CODIGO_FILIAL = FILIAL AND MOV_STATUS = 1 AND MOV_DOCUMENT_TYPE = REC_CODIGO_DOCUMENTO
GROUP BY MOV_DATA
UNION
SELECT DISTINCT DATE(MOV_DATA_LANCAMANTO) AS DATA_TRANSACAO, SUM(MOV_VALOR- FAT_TROCO) AS VALOR FROM FIN_MOVIMENTO_CAIXA  
INNER JOIN FIN_MOVIMENTO_FATURA ON MOV_CODIGO_MOVIMENTO = MOV_CODIGO
INNER JOIN COM_FATURA_CLIENTE ON FAT_CODIGO = MOV_CODIGO_FATURA 
INNER JOIN COM_DOCUMENTO F ON F.DOC_CODIGO = FAT_CODIGO_DOCUMENTO 
WHERE MOV_TRANSACAO='E' AND  MOV_DATA BETWEEN PERIODO_INICIO AND PERIODO_FIM
AND FAT_STATUS = 1 AND MOV_CODIGO_FILIAL = FILIAL AND MOV_STATUS = 1 
GROUP BY MOV_DATA;

END IF;
 
END
;;
delimiter ;

-- ----------------------------
-- Procedure structure for stp_FIN_MOVIMENTO_CUSTOMER_RECEIPTS_RESUME
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_FIN_MOVIMENTO_CUSTOMER_RECEIPTS_RESUME`;
delimiter ;;
CREATE PROCEDURE `stp_FIN_MOVIMENTO_CUSTOMER_RECEIPTS_RESUME`(IN `YEAR_FROM` int,IN `YEAR_UNTIL` int, IN FILIAL int, IN ENTITY_ID INT, ACCOUNT_ID VARCHAR(20))
BEGIN
	#Routine body goes here...
SELECT MONTH(MOV_DATA_LANCAMANTO) AS MES, YEAR(MOV_DATA) AS ANO, SUM(MOV_VALOR - FAT_TROCO) AS VALOR FROM FIN_MOVIMENTO_CAIXA 
INNER JOIN FIN_MOVIMENTO_FATURA ON MOV_CODIGO_MOVIMENTO = MOV_CODIGO
INNER JOIN COM_FATURA_CLIENTE ON FAT_CODIGO = MOV_CODIGO_FATURA 
INNER JOIN COM_DOCUMENTO F ON F.DOC_CODIGO = FAT_CODIGO_DOCUMENTO
WHERE MOV_TRANSACAO='E' AND YEAR(MOV_DATA) BETWEEN YEAR_FROM AND YEAR_UNTIL AND MOV_CODIGO_FILIAL = FILIAL
AND MOV_STATUS = 1
GROUP BY EXTRACT(YEAR_MONTH FROM MOV_DATA)
UNION 
SELECT MONTH(MOV_DATA_LANCAMANTO) AS MES, YEAR(MOV_DATA) AS ANO, SUM(MOV_VALOR) AS VALOR FROM FIN_MOVIMENTO_CAIXA 
INNER JOIN COM_RECIBO_CLIENTE ON REC_CODIGO = MOV_DOCUMENT_ID  
LEFT JOIN COM_DOCUMENTO L ON L.DOC_CODIGO = REC_CODIGO_DOCUMENTO 
WHERE MOV_TRANSACAO='E' AND YEAR(MOV_DATA) BETWEEN YEAR_FROM AND YEAR_UNTIL AND MOV_CODIGO_FILIAL = FILIAL
AND MOV_STATUS = 1 AND REC_CODIGO_DOCUMENTO = MOV_DOCUMENT_TYPE
GROUP BY EXTRACT(YEAR_MONTH FROM MOV_DATA);
 
END
;;
