-- ----------------------------
-- Procedure structure for stp_FIN_MOVIMENTO_OBTERPORFILTRO
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_FIN_MOVIMENTO_OBTERPORFILTRO`;
delimiter ;;
CREATE PROCEDURE `stp_FIN_MOVIMENTO_OBTERPORFILTRO`(DATA_MOV_INI DATE, DATA_MOV_TERM DATE, NATUREZA VARCHAR(1), MOEDA INT, CONTA VARCHAR(20), DESCRICAO VARCHAR(120), BANCO INT, COMPROVANTE VARCHAR(20), FILIAL INT, FILTRO VARCHAR(1))
BEGIN
	#Routine body goes here...
DECLARE v_CONSULTA LONGTEXT;

IF(NATUREZA ="-1") THEN
SET NATUREZA = "";
END IF;
SET v_CONSULTA =CONCAT(" 
SELECT DISTINCT  MOV_CODIGO, CONCAT(BANC_SIGLA, ' - ', MOV_CONTA_BANCARIA, ' - ', MOE_SIGLA) AS MOV_CONTA_BANCARIA, MOV_DATA, MOV_DATA_LANCAMANTO, MOV_CODIGO_MOEDA, MOV_TRANSACAO, MOV_MEIO_PAGAMENTO, MOV_UTILIZADOR,
MOV_CODIGO_FILIAL, MOV_DESCRICAO, MOV_FLUXO_CAIXA, MOV_VALOR, MOV_ENTIDADE, MOV_OBSERVACOES, MOV_NUM_DOC_PAGTO, MOV_STATUS, MOV_DOCUMENT_TYPE,
MOV_DOCUMENT_ID, MOV_TREASURE_SERIE_ID, MOV_CONCILIADA, MOV_REAL, MOV_REFERENCIA, MOV_NUMERACAO,
MOE_SIGLA, MOE_DESCRICAO, BANC_SIGLA, 
CASE WHEN MOV_TRANSACAO ='S' THEN PAG_DESCRICAO_OUTCOME
ELSE PAG_DESCRICAO END AS PAG_DESCRICAO, RUB_DESCRICAO, DOC_DESCRICAO, SER_DESCRICAO, RUB_RUBRICA_ID   FROM FIN_MOVIMENTO_CAIXA
INNER JOIN FIN_MOEDA ON MOE_CODIGO = MOV_CODIGO_MOEDA
INNER JOIN GER_MEIO_PAGAMENTO P ON P.PAG_CODIGO = MOV_MEIO_PAGAMENTO
INNER JOIN FIN_CONTA_BANCARIA ON CONT_NUMERO = MOV_CONTA_BANCARIA
INNER JOIN FIN_BANCO ON BANC_CODIGO = CONT_CODIGO_BANCO
LEFT JOIN  FIN_CONTAS_TESOURARIA ON RUB_CODIGO = MOV_FLUXO_CAIXA
INNER JOIN COM_DOCUMENTO ON DOC_CODIGO = MOV_DOCUMENT_TYPE
LEFT JOIN COM_SERIE_DOCUMENTACAO ON SER_CODIGO = MOV_TREASURE_SERIE_ID

WHERE MOV_STATUS = 1 AND MOV_DESCRICAO LIKE '%",DESCRICAO,"%'");
IF(FILIAL >0) THEN
    SET v_CONSULTA=CONCAT(v_CONSULTA," AND MOV_CODIGO_FILIAL=", FILIAL);
END IF; 
  
IF(MOEDA > 0) THEN
 SET v_CONSULTA=CONCAT(v_CONSULTA," AND M.MOV_CODIGO_MOEDA=", MOEDA);
END IF;

IF(CONTA!='' AND CONTA!='-1' AND CONTA IS NOT NULL) THEN 
 SET v_CONSULTA = CONCAT(v_CONSULTA," AND MOV_CONTA_BANCARIA = '",CONTA,"'");
END IF;

  IF(COMPROVANTE!='' AND CONTA IS NOT NULL) THEN 
      SET v_CONSULTA = CONCAT(v_CONSULTA," AND MOV_NUM_DOC_PAGTO LIKE'%",COMPROVANTE,"%'");
  END IF;

IF(BANCO!='' AND BANCO!='-1' AND BANCO IS NOT NULL) THEN 
      SET v_CONSULTA = CONCAT(v_CONSULTA," AND CONT_CODIGO_BANCO = ", BANCO);
END IF;

IF(NATUREZA IS NOT NULL AND NATUREZA!='-1' AND NATUREZA!='') THEN  
 SET v_CONSULTA = CONCAT(v_CONSULTA," AND MOV_TRANSACAO = '",NATUREZA,"'"); 
END IF;

IF(FILTRO ='L') THEN
     
	 IF(DATA_MOV_INI IS NOT NULL AND DATA_MOV_TERM IS NOT NULL) 	THEN
		 SET v_CONSULTA = CONCAT(v_CONSULTA ," AND MOV_DATA_LANCAMANTO BETWEEN  '", DATA_MOV_INI,"' AND '", DATA_MOV_TERM, "'");
		ELSE
		IF(DATA_MOV_INI IS NOT NULL AND DATA_MOV_TERM IS  NULL) 	THEN
		 SET v_CONSULTA = CONCAT(v_CONSULTA ," AND MOV_DATA_LANCAMANTO >= '",DATA_MOV_INI, "'");
		ELSE
		IF(DATA_MOV_INI IS NULL AND DATA_MOV_TERM IS NOT NULL) 	THEN
		 SET v_CONSULTA = CONCAT(v_CONSULTA ," AND MOV_DATA_LANCAMANTO <= '",DATA_MOV_TERM, "'");
		END IF;
		END IF;
		END IF;
 ELSE
    IF(DATA_MOV_INI IS NOT NULL AND DATA_MOV_TERM IS NOT NULL) 	THEN
		 SET v_CONSULTA = CONCAT(v_CONSULTA ," AND MOV_DATA BETWEEN  '",DATA_MOV_INI, "' AND '", DATA_MOV_TERM, "'");
		ELSE
		IF(DATA_MOV_INI IS NOT NULL AND DATA_MOV_TERM IS  NULL) 	THEN
		 SET v_CONSULTA = CONCAT(v_CONSULTA ," AND MOV_DATA >= '",DATA_MOV_INI, "'");
		ELSE
		IF(DATA_MOV_INI IS NULL AND DATA_MOV_TERM IS NOT NULL ) 	THEN
		 SET v_CONSULTA = CONCAT(v_CONSULTA ," AND MOV_DATA <= '",DATA_MOV_TERM, "'");
		END IF;
		END IF;
		END IF;
  END IF;
 
SET v_CONSULTA = CONCAT(v_CONSULTA, " ORDER BY MOV_DATA");

#SELECT v_CONSULTA;
 

 SET @stmt_str = v_CONSULTA;
 PREPARE stmt FROM @stmt_str;
 EXECUTE stmt;
 DEALLOCATE PREPARE stmt;
END
;;
delimiter ;

