delimiter ;;
CREATE PROCEDURE `stp_FIN_MOVIMENTO_PERIODICO_ADICIONAR`(IN `CODIGO` int,IN `DESCRICAO` varchar(120),IN `INICIO` date,IN `TERMINO` date,IN `PERIODICIDADE` varchar(1),IN `DIAS` int,IN `NATUREZA` varchar(1),IN `ENTIDADE` int,IN `RUBRICA` int,IN `VALOR` decimal(18,5),IN `MOEDA` int,IN `CAMBIO` decimal(18,5),IN `DOCUMENT_ID` int,IN `ACCOUNT_ID` int,IN `ESTADO` bit,IN `UTILIZADOR` varchar(20), OUT LSTID INT)
BEGIN
	#Routine body goes here...

IF(CODIGO <=0) THEN
INSERT INTO FIN_MOVIMENTOS_PERIODICOS(PROG_CODIGO,
PROG_DESCRICAO,
PROG_FROM,
PROG_UNTIL,
PROG_PERIODICIDADE,
PROG_DIAS,
PROG_NATUREZA,
PROG_ENTIDADE_ID,
PROG_RUBRICA_ID,
PROG_VALOR,
PROG_MOEDA_ID,
PROG_CAMBIO,
PROG_DOCUMENT_ID,
PROG_ACCOUNT_ID,
