PROG_PERIODICIDADE = PERIODICIDADE,
PROG_DIAS = DIAS,
PROG_NATUREZA = NATUREZA,
PROG_ENTIDADE_ID = ENTIDADE,
PROG_RUBRICA_ID = RUBRICA,
PROG_VALOR = VALOR,
PROG_MOEDA_ID = MOEDA,
PROG_CAMBIO = CAMBIO,
PROG_DOCUMENT_ID = DOCUMENTO,
PROG_ACCOUNT_ID = ACCOUNT_ID,
PROG_STATUS = ESTADO, 
PROG_UPDATED_BY = UTILIZADOR,
PROG_UPDATED_DATE = NOW()
WHERE PROG_CODIGO = CODIGO;
SET LSTID = CODIGO;
END
;;
