-- ----------------------------
-- Procedure structure for stp_FIN_MOVIMENTO_RESUMO_PORMEIOPAGAMENTO
-- ----------------------------
DROP PROCEDURE IF EXISTS `stp_FIN_MOVIMENTO_RESUMO_PORMEIOPAGAMENTO`;
delimiter ;;
CREATE PROCEDURE `stp_FIN_MOVIMENTO_RESUMO_PORMEIOPAGAMENTO`(IN `INICIO` date,IN `TERMINO` date, FILIAL INT, UTILIZADOR VARCHAR(20), WAREHOUSE_ID INT)
BEGIN
	#Routine body goes here... 
DECLARE v_CON_ LONGTEXT;
DECLARE PERFIL_ID INT DEFAULT 0;
DECLARE UTILIZADOR_ID INT DEFAULT 0;

SELECT UTI_CODIGO_PERFIL, UTI_CODIGO INTO PERFIL_ID, UTILIZADOR_ID FROM SIS_UTILIZADOR WHERE UTI_UTILIZADOR = UTILIZADOR;

SET v_CON_ = CONCAT("
SELECT PAG_CODIGO, PAG_DESCRICAO, SUM(VALOR) AS ACUMULADO FROM
(
    SELECT PAG_CODIGO, PAG_DESCRICAO, 
		(CASE WHEN MOV_TRANSACAO='E' THEN
		  MOV_VALOR 
		  WHEN (MOV_TRANSACAO='S' AND MOV_VALOR > 0 -MOV_TRANSACAO) THEN (MOV_VALOR *(-1))
     ELSE 0 
    END) AS VALOR
		FROM FIN_MOVIMENTO_CAIXA
		INNER JOIN GER_MEIO_PAGAMENTO ON PAG_CODIGO = MOV_MEIO_PAGAMENTO 
    INNER JOIN FIN_MOVIMENTO_FATURA ON MOV_CODIGO_MOVIMENTO = MOV_CODIGO
    INNER JOIN COM_FATURA_CLIENTE ON FAT_CODIGO = MOV_CODIGO_FATURA 
		WHERE MOV_DATA_LANCAMANTO >='", INICIO, "' AND MOV_STATUS = 1 AND DATE(MOV_DATA_LANCAMANTO) <='", TERMINO,"'
		AND MOV_CODIGO_FILIAL =", FILIAL," AND FAT_STATUS=1 AND FAT_STATUS_DOCUMENTO =8 "); 


IF(WAREHOUSE_ID > 0) THEN
 SET v_CON_=CONCAT(v_CON_, " AND FAT_CODIGO_ARMAZEM = ", WAREHOUSE_ID);
ELSE
 SET v_CON_=CONCAT(v_CON_," AND FAT_CODIGO_ARMAZEM IN (SELECT WAR_PERM_ARMAZEM_ID FROM GER_ARMAZEM_PERMISSOES
WHERE WAR_PERM_STATUS = 1 AND (WAR_PERM_PERFIL_ID =",PERFIL_ID," OR WAR_PERM_UTILIZADOR_ID = ",UTILIZADOR_ID,"))");
END IF;
 
SET v_CON_=CONCAT(v_CON_," UNION
SELECT PAG_CODIGO, PAG_DESCRICAO, 
		(CASE WHEN MOV_TRANSACAO='E' THEN
		  MOV_VALOR 
		  WHEN (MOV_TRANSACAO='S' AND MOV_VALOR > 0 -MOV_TRANSACAO) THEN (MOV_VALOR *(-1))
     ELSE 0 
    END) AS VALOR
		FROM FIN_MOVIMENTO_CAIXA
		INNER JOIN GER_MEIO_PAGAMENTO ON PAG_CODIGO = MOV_MEIO_PAGAMENTO 
    INNER JOIN COM_RECIBO_CLIENTE ON REC_CODIGO_DOCUMENTO = MOV_DOCUMENT_TYPE 
		WHERE MOV_DATA_LANCAMANTO >='", INICIO, "' AND MOV_STATUS = 1 AND DATE(MOV_DATA_LANCAMANTO) <='", TERMINO,"'
		AND MOV_CODIGO_FILIAL =", FILIAL," AND REC_STATUS = 1 AND REC_CODIGO = MOV_DOCUMENT_ID
) AS PAYMENT_SUMMARY GROUP BY PAG_DESCRICAO");
#SELECT v_CON_;
SET @stmt_str = v_CON_;
PREPARE stmt FROM @stmt_str;
EXECUTE stmt;
DEALLOCATE PREPARE stmt;

END
