	DECLARE PRODCUT_LIST CURSOR FOR SELECT ART_CODIGO FROM GER_ARTIGO;
	DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE;
IF(CODIGO <=0) THEN

	OPEN PRODCUT_LIST;


	read_loop: LOOP


	FETCH PRODCUT_LIST INTO PRODUCT_ID;
		IF done THEN
		 LEAVE read_loop;
		END IF;

	IF(PRODUCT_ID < 10) THEN
		SET BARCODE = CONCAT("50521210",PRODUCT_ID);
	ELSE IF(PRODUCT_ID >=10 AND PRODUCT_ID < 100) THEN
		SET BARCODE = CONCAT("5052121",PRODUCT_ID);
